INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2020-2029-nevsky-prospekt', 'Санкт-Петербург 2020-2029 — «Невский проспект»', 'Игрок проходит от Адмиралтейства до Александро-Невской лавры, слушая истории, встречая музыкантов и собирая культурные артефакты.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "start_at_admiralty", "text": "Начать прогулку у шпиля Адмиралтейства, получить аудиогид и задание фиксировать визуальные заметки", "type": "interact", "target": "nevsky_admiralty_start", "count": 1, "optional": false}, {"id": "visit_kazan_cathedral", "text": "Остановиться у колоннады Казанского собора, прослушать лекцию о религиозной и культурной роли места, сделать фотоснимок", "type": "interact", "target": "nevsky_kazan_cathedral", "count": 1, "optional": false}, {"id": "visit_singer_house", "text": "Войти в Дом Зингера, встретить NPC-литератора, приобрести сувенир или книгу, заполучить цитату Гоголя", "type": "interact", "target": "nevsky_singer_house", "count": 1, "optional": false}, {"id": "cross_liteiny_intersection", "text": "Пересечь оживлённый перекрёсток Литейного и Литейного моста, послушать уличных музыкантов, собрать аудиосэмплы для LiveOps", "type": "perform", "target": "nevsky_liteiny_crossroads", "count": 1, "optional": false}, {"id": "finish_at_lavra", "text": "Завершить маршрут у Александро-Невской лавры, получить благословение и финальный обзор городской панорамы", "type": "interact", "target": "nevsky_lavra_finale", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "currency": 0, "items": [], "reputation": {"Petersburg_Culture": 5}, "flags": ["nevsky_prospekt_walk_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2020-2029-nevsky-prospekt", "title": "Санкт