INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-european-silicon', 'Берлин 2061-2077 — «Европейский кремний»', 'Игрок мобилизует инвестиции, таланты и голоса ЕС, определяя распределение звания «Европейский Silicon Valley».', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-european-silicon", "title": "Берлин 2061-2077 — «Европейский кремний»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "tech-policy", "europe"], "topics": ["economic-strategy", "urban-competition"], "related_systems": ["narrative-service", "reputation-system", "diplomacy-system"], "related_documents": [{"id": "canon-lore-europe-berlin-2061-2077", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2061-2077/quest-033-european-silicon.md", "visibility": "internal", "audience": ["narrative", "quest-design", "strategy"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Исходный Markdown не фиксировал политико-экономические выборы в борьбе Берлина за статус tech-столицы.", "goal": "Структурировать квест о конкуренции с Лондоном и Парижем с влиянием на региональные метрики и альянсы.", "essence": "Игрок мобилизует инвестиции, таланты и голоса ЕС, определяя распределение звания «Европейский Silicon Valley».", "key_points": ["Берлин конкурирует с Лондоном и Парижем на фоне пост-Brexit динамики.", "Решение игрока определяет экономические бонусы и политические последствия для региона.", "Возможен компромисс, укрепляющий три города в единой tech-сети."]}, "content": {"sections": [{"id": "lobbying", "title": "Лоббирование инвестиций", "body": "Игрок формирует коалицию инвесторов и госструктур, продвигая льготы и инфраструктуру.\\nУспех повышает экономические модификаторы Берлина.\\n", "mechanics_links": [], "assets": []}, {"id": "talent", "title": "Привлечение талантов", "body": "Проводятся ярмарки в университетах ЕС, конкурсы инноваторов и релокации команд.\\nРешения влияют на доступ к уникальным специалистам и проектам.\\n", "mechanics_links": [], "assets": []}, {"id": "campuses", "title": "Открытие тех-кампусов", "body": "Корпорации выбирают площадку: Берлин, Лондон или Париж.\\nИгрок балансирует налоговые льготы и требования сообщества.\\n", "mechanics_links": [], "assets": []}, {"id": "vote", "title": "Голосование за tech-столицу", "body": "Финальное собрание ЕС определяет победителя или альянс трёх мегаполисов.\\nВыбор задаёт длительные репутационные и экономические эффекты.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP, глобальные экономические модификаторы для выбранного города.\\n- Разблокировка cross-city сюжетов с Лондоном и Парижем.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "European Silicon Valley", "definition": "Образное название для ведущего tech-хаба Европы, за который соперничают Берлин, Лондон и Париж."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Европейский кремний» в knowledge-entry с акцентом на стратегический выбор."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

