INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2020-2029-aurora-revolution', 'Санкт-Петербург 2020-2029 — «Революция “Авроры”»', 'Игрок собирает революционеров, захватывает крейсер, даёт залп и штурмует корп-цитадель, выбирая исход восстания.', 'main', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "rally_revolutionaries", "text": "Объединить активистов, минуя слежку корпораций, убедить фракции, подготовить лозунги и ресурсы для восстания", "type": "perform", "target": "aurora_revolution_rally", "count": 1, "optional": false}, {"id": "capture_aurora", "text": "Захватить музейный крейсер «Аврора», развесить красные флаги и подготовить пушку к символическому выстрелу", "type": "perform", "target": "aurora_revolution_capture", "count": 1, "optional": false}, {"id": "assault_corp_citadel", "text": "После залпа вести штурм корпоративного дворца (аналог Зимнего), используя тактические решения и поддержку толпы", "type": "perform", "target": "aurora_revolution_assault", "count": 1, "optional": false}, {"id": "resolve_revolution", "text": "Принять решение о исходе революции: революция побеждает, власти подавляют восстание или стороны заключают реформаторский компромисс", "type": "interact", "target": "aurora_revolution_resolution", "count": 1, "optional": false}]'::jsonb, '{"experience": 10000, "currency": 0, "items": [], "reputation": {"Revolutionaries": 100}, "flags": ["aurora_revolution_completed"]}'::jsonb, '[{"id": "revolution_victory", "condition": "resolution == \\"revolution_victory\\"", "objectives": [{"id": "establish_new_order", "text": "Установить новый революционный порядок в Петербурге", "type": "interact", "target": "aurora_revolution_new_order", "count": 1, "optional": false}], "rewards": {"experience": 5000, "currenc