INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2030-2039-museum-island', 'Берлин 2030-2039 — Музейный остров', 'Игрок проходит все музеи острова за один день, повышая параметр «Культура» и открывая музейные линии Берлина.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2030-2039-museum-island", "title": "Берлин 2030-2039 — Музейный остров", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "museum", "culture"], "topics": ["cultural-tour", "timeline-author"], "related_systems": ["narrative-service", "progression-service"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-011-museum-island.md", "visibility": "internal", "audience": ["narrative", "quest-design", "localization"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Культурный марафон по Музейному острову не был формализован и терял образовательные механики.", "goal": "Описать маршрут по пяти музеям с AR-реконструкциями и единым билетом в структуре знаний.", "essence": "Игрок проходит все музеи острова за один день, повышая параметр «Культура» и открывая музейные линии Берлина.", "key_points": ["Единый маршрут охватывает Pergamon, Neues, Alte Nationalgalerie, Bode и Altes Museum.", "AR-реконструкции оживляют древние артефакты, включая ворота Иштар и зал Нефертити.", "Завершение даёт титул «Знаток Острова Муз» и значительный прирост культуры."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Museumsinsel — UNESCO-объект на Шпрее с пятью музеями мирового уровня.\\nКвест подчеркивает роль Берлина как культурной столицы и усиливает образовательные линии.\\n", "mechanics_links": [], "assets": []}, {"id": "itinerary", "title": "Маршрут по музеям", "body": "1. Купить единый билет и получить карту маршрута.\\n2. Посетить Pergamon Museum: ворота Иштар, Пергамский алтарь.\\n3. Neues Museum: бюст Нефертити и египетские коллекции.\\n4. Alte Nationalgalerie: европейская живопись XIX века.\\n5. Bode Museum: скульптуры, византийское искусство и нумизматика.\\n6. Altes Museum: античные экспонаты Афин на Шпрее.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/culture-tours.yaml"], "assets": []}, {"id": "interactions", "title": "Игровые взаимодействия", "body": "- AR-реконструкции древних храмов и городов.\\n- Сканирование ключевых экспонатов для коллекции «Классика мира».\\n- Таймер культурного марафона поддерживает темп прохождения (6–8 часов).\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/collection-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP.\\n- Издержки: −50 едди (единый билет).\\n- +30 к параметру «Культура».\\n- Титул «Знаток Острова Муз».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Культурный марафон, образовательные события, AR-туры и коллекционирование артефактов.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/world/culture-tours.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 54, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-berlin-2030-2039-museum-island"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Связаны механики маршрута и коллекций, обновлена версия и дата."}, {"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован квест «Музейный остров» в YAML с описанием культурного марафона."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

