INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2030-2039-currywurst-empire', 'Берлин 2030-2039 — Империя карривурст', 'Игрок строит империю уличной еды, оптимизирует рецепт карривурста и захватывает районы мегаполиса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2030-2039-currywurst-empire", "title": "Берлин 2030-2039 — Империя карривурст", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.2.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "street-food", "economy"], "topics": ["business-simulation", "culinary"], "related_systems": ["narrative-service", "economy-service", "crafting-service"], "related_documents": [{"id": "canon-quest-berlin-2020-2029-currywurst-vendor", "relation": "precedes"}, {"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2030-2039/quest-012-currywurst-empire.md", "visibility": "internal", "audience": ["narrative", "economy", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История о создании сети карривурст-будок не отражала бизнес-механику и рост бренда.", "goal": "Формализовать управление уличной сетью Берлина с упором на рецепт, конкуренцию и пассивный доход.", "essence": "Игрок строит империю уличной еды, оптимизирует рецепт карривурста и захватывает районы мегаполиса.", "key_points": ["Карривурст остаётся гастрономическим символом Берлина с 70 млн порций в год.", "Секретный соус, мини-игра рецепта и выбор локаций определяют успех.", "Финал присваивает титул «Currywurst König» и даёт стабильный доход."]}, "content": {"sections": [{"id": "setup", "title": "Старт бизнеса", "body": "- Приобрести первую будку и оформить лицензию у городских органов.\\n- Собрать ингредиенты и открыть мини-игру по созданию уникального соуса.\\n- Настроить бренд и маркетинговые лозунги.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml", "mechanics/economy/business-simulation.yaml"], "assets": []}, {"id": "expansion", "title": "Расширение сети", "body": "- Открыть до десяти точек в разных районах (Mitte, Kreuzberg, Neukölln, Prenzlauer Berg).\\n- Управлять персоналом, логистикой соусов и снабжением булочек.\\n- Проводить дегустации и события Live Ops для роста репутации.\\n", "mechanics_links": ["mechanics/economy/market-dynamics.yaml", "mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "competition", "title": "Конкуренция и защита бренда", "body": "- Сражаться с конкурентами за лучшие локации и лицензии.\\n- Проводить кампании «Лучший карривурст Берлина» и защищать рецепт от шпионажа.\\n- Балансировать цены и качество для удержания очередей.\\n", "mechanics_links": ["mechanics/economy/business-simulation.yaml", "mechanics/world/reputation-system.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP.\\n- Пассивный доход: 20 000 едди в неделю.\\n- Титул «Currywurst König».\\n- Повышение репутации кулинарной линии Берлина.\\n", "mechanics_links": ["mechanics/economy/rewards.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 54, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-berlin-2030-2039-currywurst-empire"], "blockers": []}, "history": [{"version": "0.2.0", "date": "2025-12-12", "author": "content_team", "changes": "Добавлены ссылки на экономические механики, обновлены версия и дата."}, {"version": "0.1.0", "date": "2025-11-12", "author": "narrative_team", "changes": "Конвертирован квест «Империя карривурст» в YAML с бизнес-механиками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

