INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2029-mariinsky-opera', 'Санкт-Петербург 2020-2029 — Мариинский театр', 'Игрок добывает билет, подготавливает образ, посещает представление и участвует в светском общении во время антрактов.', 'side', 5, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "acquire_ticket", "text": "Приобрести редкий билет в Мариинский театр или получить приглашение от мецената", "type": "interact", "target": "mariinsky_ticket_acquisition", "count": 1, "optional": false}, {"id": "prepare_formal_attire", "text": "Подобрать формальный гардероб и аксессуары, соответствующие дресс-коду театра", "type": "interact", "target": "mariinsky_formal_attire", "count": 1, "optional": false}, {"id": "attend_performance", "text": "Посетить представление (опера или балет), отмечая ключевые сцены и наслаждаясь постановкой", "type": "interact", "target": "mariinsky_performance", "count": 1, "optional": false}, {"id": "socialize_during_intermission", "text": "В антракте вести переговоры, дегустировать шампанское и обмениваться визитками с культурной элитой", "type": "interact", "target": "mariinsky_intermission_social", "count": 1, "optional": false}, {"id": "complete_evening", "text": "Завершить вечер аплодисментами и получить обратную связь от культурных кураторов", "type": "interact", "target": "mariinsky_evening_completion", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "currency": -500, "items": [], "reputation": {"Petersburg_Culture": 10}, "flags": ["mariinsky_opera_achievement"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2029-mariinsky-opera", "title": "Санкт-Петербург 2020-2029 — Мариинский театр", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T21:50:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "opera", "culture"], "topics": ["cultural_events", "high_society"], "related_systems": ["narrative-service", "social-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2020-2029/quest-008-mariinsky-opera.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальной линии Петербурга требовался элитный культурный опыт, подчеркивающий атмосферу императорского театра.", "goal": "Организовать визит игрока на оперу или балет в Мариинском театре с соблюдением этикета и взаимодействием с высшим обществом.", "essence": "Игрок добывает билет, подготавливает образ, посещает представление и участвует в светском общении во время антрактов.", "key_points": ["Билеты дефицитны и требуют статуса или связи.", "Антракт превращается в социальную арену для переговоров и повышения репутации.", "Визит дает временный харизматический бафф и открывает контакты культурной элиты."]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "acquire_ticket", "text": "Приобрести редкий билет в Мариинский театр или получить приглашение от мецената", "type": "interact", "target": "mariinsky_ticket_acquisition", "count": 1, "optional": false}, {"id": "prepare_formal_attire", "text": "Подобрать формальный гардероб и аксессуары, соответствующие дресс-коду театра", "type": "interact", "target": "mariinsky_formal_attire", "count": 1, "optional": false}, {"id": "attend_performance", "text": "Посетить представление (опера или балет), отмечая ключевые сцены и наслаждаясь постановкой", "type": "interact", "target": "mariinsky_performance", "count": 1, "optional": false}, {"id": "socialize_during_intermission", "text": "В антракте вести переговоры, дегустировать шампанское и обмениваться визитками с культурной элитой", "type": "interact", "target": "mariinsky_intermission_social", "count": 1, "optional": false}, {"id": "complete_evening", "text": "Завершить вечер аплодисментами и получить обратную связь от культурных кураторов", "type": "interact", "target": "mariinsky_evening_completion", "count": 1, "optional": false}], "rewards": {"experience": 1500, "currency": -500, "items": [], "reputation": {"Petersburg_Culture": 10}, "flags": ["mariinsky_opera_achievement"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в Мариинский театр — пространство императорской роскоши. Надо соблюсти дресс-код, подготовиться к светскому\\nобщению и насладиться постановкой мирового уровня.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Приобрести редкий билет или получить приглашение от мецената.\\n2. Подобрать формальный гардероб и аксессуары, соответствующие дресс-коду.\\n3. Посетить представление (опера или балет), отмечая ключевые сцены.\\n4. В антракте вести переговоры, дегустировать шампанское и обмениваться визитками.\\n5. Завершить вечер аплодисментами и получить обратную связь от культурных кураторов.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Сине-золотые интерьеры, хрустальные люстры и императорские ложи создают атмосферу XIX века. В фойе выставлены экспонаты с историей\\nтеатра, звучат отсылки к «Лебединому озеру» и легендарным постановкам.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 500 XP, -500 еддис на расходы вечера.\\n- Харизма +15% на 48 игровых часов.\\n- Репутация «Культура СПб» +10 и контакты с элитой.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Культурное событие высокого общества, социальные проверки, ограниченные билеты, временные баффы и развитие сетевых связей.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с культурной веткой Санкт-Петербурга и международными театральными квестами, усиливает сюжетные линии о культурной дипломатии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 801, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики культурного события и социального взаимодействия."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Мариинский театр» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

