-- Quest: quest-dallas-2029-004-oil-legacy
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-dallas-2029-004-oil-legacy', 'Даллас — нефтяное наследие', 'Игрок исследует Southfork Ranch, анализирует историю нефтяных семей и принимает решения о сотрудничестве с фракциями.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-dallas-2029-004-oil-legacy", "title": "Даллас — нефтяное наследие", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-11T06:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["dallas", "oil", "economy"], "topics": ["city_storylines", "economic_history"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "master-timeline", "relation": "references"}, {"id": "quest-system", "relation": "aligns"}, {"id": "github-issue-332", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/332", "relation": "migrated_to", "migrated_at": "2025-11-22T19:45:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/dallas/2020-2029/quest-004-oil-legacy.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Текст упоминал нефтяных баронов и сериал «Dallas», но не давал игровой структуры и экономических решений.", "goal": "Превратить изучение нефтяного наследия в серию сюжетных и экономических выборов для игрока.", "essence": "Игрок исследует Southfork Ranch, анализирует историю нефтяных семей и принимает решения о сотрудничестве с фракциями.", "key_points": ["История Texas oil boom спроектирована через интерактивные экспонаты и архивы.", "Выбор между традиционалистами и зелёными инвесторами влияет на экономику города.", "Квест связывает сериал «Dallas» с текущими политическими событиями."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Сюжет ведёт игрока по ключевым точкам нефтяного бумa: от архивов начала XX века до влияния сериала «Dallas». Посещение\\nSouthfork Ranch раскрывает личные истории семей, а современные переговоры показывают, как нефть продолжает формировать город.\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"], "assets": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "Texas oil boom, культовый ранчо Southfork и образ нефтяных баронов остаются символами Далласа. Экспозиции демонстрируют,\\nкак богатство формировало инфраструктуру и социальное расслоение города.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы квеста", "body": "1. Изучить архивные материалы о начале нефтяного бума в Dallas Petroleum Museum.\\n2. Посетить Southfork Ranch, поговорить с гидами и восстановить сцены сериала «Dallas».\\n3. Провести интервью с потомками нефтяных семей по поводу современных инвестиций.\\n4. Сделать выбор: поддержать классическую нефтяную корпорацию или предложить диверсификацию в зелёную энергетику.\\n5. Зафиксировать последствия выбора для экономики и отношений фракций.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест даёт 2500 опыта, не требует трат валюты, увеличивает параметр «Экономика» на 20 и начисляет ачивку «Нефтяной барон».\\nВыбор ветки задаёт модификаторы дохода города.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Интеграции и последствия", "body": "Economy-service фиксирует изменение потоков капитала, narrative-service раскрывает дополнительные сюжетные ветви про\\nполитическое давление, а социальные механики отслеживают лояльность кластера нефтяных семей.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 332, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Структурирован квест «Нефтяное наследие» с экономическими ветвлениями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

