INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-atlanta-2029-005-civil-war', 'Атланта: наследие Гражданской войны', 'Игрок изучает события 1864 года, посещает Cyclorama и принимает решение, как представить наследие войны жителям мегаполиса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-atlanta-2029-005-civil-war", "title": "Атланта: наследие Гражданской войны", "document_type": "canon", "category": "city-quest", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:15:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["atlanta", "history", "civil-war"], "topics": ["city-lore"], "related_systems": ["social-mechanics", "narrative-service"], "related_documents": [{"id": "github-issue-296", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/296", "relation": "migrated_to", "migrated_at": "2025-11-22T19:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-005-civil-war-history.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание роли Атланты в Гражданской войне было списком фактов без интерактивных сцен и последствий.", "goal": "Дать структуру музея, битвы и культурного переосмысления с выбором игрока, влияющим на историческую линию города.", "essence": "Игрок изучает события 1864 года, посещает Cyclorama и принимает решение, как представить наследие войны жителям мегаполиса.", "key_points": ["Подчёркнуто разрушение Атланты и символ Феникса.", "Игрок взаимодействует с музейными экспозициями и ветеранскими сообществами.", "Выбор влияет на развитие исторических событий в narrative-service."]}, "content": {"sections": [{"id": "overview", "title": "Кратко", "body": "- Квестовый ID: `ATLANTA-2029-005`\\n- Формат: исторический сценарий, сложность средняя, solo, длительность 2–4 часа.\\n- Локации: Atlanta History Center, Cyclorama, музей «Gone with the Wind».\\n", "mechanics_links": ["mechanics/quests/quest-system.yaml"]}, {"id": "chronology", "title": "Ход исследования", "body": "1. Изучить архивы битвы за Атланту в 1864 году.\\n2. Посетить Cyclorama и отследить ключевые моменты марша Шермана.\\n3. Проанализировать последствия политики «выжженной земли» для города.\\n4. Оценить культурное влияние романа «Gone with the Wind» и современной критики.\\n", "mechanics_links": ["mechanics/social/social-mechanics-overview.yaml"]}, {"id": "decisions", "title": "Решения игрока", "body": "- Поддержать памятные инициативы ветеранов и усилить патриотическую линию.\\n- Сфокусироваться на примирении и культурном переосмыслении.\\n- Создать интерактивный образовательный маршрут для школ.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды", "body": "- 3000 XP, расходы 15 кредитов на музейные билеты.\\n- Показатель «История» +25, ачивка «Феникс Атланты».\\n- Доступ к историческим контрактам города и уникальной эмблеме Phoenix Rising.\\n", "mechanics_links": ["mechanics/progression/progression-skills.yaml"]}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 296, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_team", "changes": "Конверсия квеста о Гражданской войне в Атланте в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.