INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-peterhof-fountains', 'Санкт-Петербург 2030-2039 — Фонтаны Петергофа', 'Игрок получает контракт, ищет чертежи, устанавливает кибер-насосы, интегрирует AR-голограммы и проводит торжественный запуск.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-peterhof-fountains", "title": "Санкт-Петербург 2030-2039 — Фонтаны Петергофа", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "peterhof", "engineering"], "topics": ["restoration", "ar_experiences"], "related_systems": ["narrative-service", "liveops-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-011-peterhof-fountains.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Петергу требовалась квестовая линия, совмещающая историческое наследие Петергофа и технологичный апгрейд фонтанов.", "goal": "Восстановить систему фонтанов и внедрить AR-шоу, превращая Петергоф в ключевой туристический узел сезона.", "essence": "Игрок получает контракт, ищет чертежи, устанавливает кибер-насосы, интегрирует AR-голограммы и проводит торжественный запуск.", "key_points": ["Проект требует инженерных навыков и координации с культурными кураторами.", "Запуск фонтанов открывает новые туристические активности и события.", "Успех повышает экономические показатели региона и культурную репутацию."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест переносит игрока в «Русский Версаль». Нужно обновить систему фонтанов Петергофа, сочетая исторические чертежи с современными\\nар-инсталляциями и подготовить запуск сезона.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Заключить контракт с дирекцией музея-заповедника.\\n2. Исследовать архивы, чтобы восстановить оригинальные гидравлические схемы.\\n3. Установить кибер-насосы и сенсоры в десяти ключевых точках парка.\\n4. Интегрировать AR-голограммы и световые сценарии в фонтаны.\\n5. Провести торжественный запуск и собрать отзывы гостей.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Большой каскад сияет золотыми статуями, морской канал ведет к Финскому заливу, а AR-драконы и голографические корабли оживляют\\nструи. Фонтаны-шутихи реагируют на движения посетителей, создавая интерактивный карнавал.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP, 100 000 еддис за выполненный контракт.\\n- Репутация «Культура СПб» +15.\\n- Доходный пассивный поток от туристического комплекса.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Инженерное строительство, интеграция AR, управление проектом, взаимодействие с культурными институциями и экономическое развитие.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Продолжает культурную линию Санкт-Петербурга, открывает события liveops по туризму и соединяется с цепочкой «Императорское наследие».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 804, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Фонтаны Петергофа» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

