-- Quest: quest-spb-2039-amber-room
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-amber-room', 'Санкт-Петербург 2030-2039 — Янтарная комната', 'Игрок получает таинственную карту, следует зацепкам через Европу, очищает бункер и сталкивается с моральным выбором.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-amber-room", "title": "Санкт-Петербург 2030-2039 — Янтарная комната", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "mystery", "treasure"], "topics": ["investigation", "high_stakes"], "related_systems": ["narrative-service", "exploration-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-012-amber-room.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходим масштабный детективный квест о пропавшей Янтарной комнате, объединяющий историю и современный хай-тек.", "goal": "Позволить игроку отследить артефакт, проникнуть в бункер и решить, возвращать ли сокровище, монетизировать или уничтожить.", "essence": "Игрок получает таинственную карту, следует зацепкам через Европу, очищает бункер и сталкивается с моральным выбором.", "key_points": ["Квест соединяет международное расследование и dungeon-геймплей.", "Выбор игрока влияет на репутацию, экономику и международную политику.", "Найденная комната становится мировым событием и меняет туристические потоки."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест раскрывает легенду о Янтарной комнате. Игрок следует конспирологическим следам, взламывает архивы, исследует бункер под\\nКалининградом и принимает решение о судьбе величайшего сокровища эпохи.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить карту от анонимного дилера и собрать сведения о возможных тайниках.\\n2. Проследовать по следу в Германии, вскрыв архивы и опросив свидетелей.\\n3. Добраться до заброшенного бункера под Кёнигсбергом и взломать систему доступа.\\n4. Пройти подземелье с ловушками, дронами-охранниками и историческими загадками.\\n5. Обнаружить Янтарную комнату (или ее копию) и принять ключевой выбор.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Янтарные панели сияют золотым светом, зеркала умножают отражения, а резные барельефы оживают благодаря проекциям. Подземный бункер\\nхранит немецкие сейфы, древние символы и цифровые ловушки, охраняющие тайну.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Базовая награда: 15 000 XP.\\n- Варианты: вернуть России (репутация +20, титул «Хранитель наследия»), продать (500 000 000 еддис, титул «Теневой магнат»),\\n  уничтожить (глобальное событие, титул «Хранитель легенд»).\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Детективное расследование, хардкорный данжен, моральный выбор, международные последствия, интеграция исторических фактов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Завершает культурную арку Царского Села, влияет на глобальные экономические и политические события, открывает цепочку «Мировые реликвии».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 805, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Янтарная комната» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

