INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-tsarskoye-selo', 'Санкт-Петербург 2030-2039 — Царское Село', 'Игрок отправляется в пригород, осматривает Екатерининский дворец, Янтарную комнату и завершает визит прогулкой по парку.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-tsarskoye-selo", "title": "Санкт-Петербург 2030-2039 — Царское Село", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "imperial-heritage", "museum"], "topics": ["cultural-tourism", "historical-education"], "related_systems": ["narrative-service", "tourism-service", "education-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-013-tsarskoye-selo.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии Санкт-Петербурга требовался квест, раскрывающий роскошь императорских резиденций.", "goal": "Показать игроку Царское Село, Янтарную комнату и дворцовые парки через экскурсионный сценарий.", "essence": "Игрок отправляется в пригород, осматривает Екатерининский дворец, Янтарную комнату и завершает визит прогулкой по парку.", "key_points": ["Императорское наследие передано через интерьер и исторические факты.", "Янтарная комната служит центральным моментом экскурсии.", "Дворцовый парк завершается размышлениями о цене роскоши."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Экскурсионный квест в Царском Селе погружает игрока в эпоху императорской России. Путешествие включает транспорт, гидов и интерактивные\\nэкспозиции, позволяющие изучить дворцовые комплексы и их историю.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Отправиться из центра Санкт-Петербурга в Царское Село.\\n2. Посетить Екатерининский дворец и пройти по Золотой анфиладе.\\n3. Осмотреть воссозданную Янтарную комнату и активировать голографические архивы.\\n4. Прогуляться по Екатерининскому парку, изучая павильоны и пруды.\\n5. Сформулировать заключительный отчёт о роли роскоши в истории империи.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Бирюзово-золотой фасад дворца, барочные интерьеры, экспозиции лицея, где учился Пушкин, и аудиогиды с рассказами о судьбах\\nимператорской семьи. В парке активируются AR-метки, показывающие строения XVIII века.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 1 500 XP.\\n- Расходы: 100 еддис на входные билеты и услуги гида.\\n- Атрибут «Культура»: +10, ачивка «Императорский визит».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Экскурсионные маршруты, аудиогиды, исторические справочники и фото-collectibles.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Продолжает дворцовую линию пригородов Санкт-Петербурга и ведёт к культурному марафону «Культурная столица».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 806, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Царское Село» адаптирован под шаблон Concept Director."}], "validation": 