INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2093-techno-legacy', 'Берлин 2078-2093 — Наследие техно', 'Игроки вступают в коллектив, выполняют культурные миссии и накапливают Legacy-очки для сохранения техно-сцены.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2093-techno-legacy", "title": "Берлин 2078-2093 — Наследие техно", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "legacy"], "topics": ["culture", "guilds"], "related_systems": ["narrative-service", "legacy-system"], "related_documents": [{"id": "quest-berlin-2050-techno-renaissance", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-042-techno-legacy.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гильдейская ветка Berlin Techno Collective не была оформлена в формате знаний и не фиксировала накопление наследия.", "goal": "Описать процесс передачи техно-культуры в следующую лигу через коллаборативные активности коллектива.", "essence": "Игроки вступают в коллектив, выполняют культурные миссии и накапливают Legacy-очки для сохранения техно-сцены.", "key_points": ["Требуется серия рейвов и обучение новой волны DJ.", "История техно фиксируется в цифровом архиве.", "Бонусы переносятся в новую лигу в виде сетов и репутации."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Berlin Techno Collective защищает культурное наследие города. Игроки помогают клубам, архивируют историю сцены и\\nготовят сообщество к перезапуску.\\n", "mechanics_links": [], "assets": []}, {"id": "missions", "title": "Этапы", "body": "1. Вступить в коллектив и пройти инициацию.\\n2. Организовать десять рейвов на легендарных площадках (Berghain, Tresor и др.).\\n3. Обучить сто новых DJ и создать цифровой архив сетов.\\n4. Сдать Legacy-очки и определить бонусы, переходящие в новую лигу.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Титул «Хранитель техно».\\n- Специальные бусты для музыкальных событий в следующей лиге.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Квест поддерживает техно-линию Берлина и взаимодействует с другими Legacy-квестами города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Наследие техно» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

