INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-cultural-capital', 'Санкт-Петербург 2030-2039 — Культурная столица', 'Игрок проходит маршрут через музеи, соборы и театры, укладываясь во временные рамки и собирая культурные отметки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-cultural-capital", "title": "Санкт-Петербург 2030-2039 — Культурная столица", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "culture", "marathon"], "topics": ["cultural-marathon", "time-challenge"], "related_systems": ["narrative-service", "tourism-service", "liveops-events-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-014-cultural-capital.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен флагманский квест, демонстрирующий культурное богатство Санкт-Петербурга.", "goal": "Организовать культурный марафон по десяти ключевым объектам за один игровой день.", "essence": "Игрок проходит маршрут через музеи, соборы и театры, укладываясь во временные рамки и собирая культурные отметки.", "key_points": ["Тайм-челлендж мотивирует планировать маршрут.", "Каждая точка раскрывает уникальный аспект города-музея.", "Завершение даёт титул и повышенную культурную репутацию."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Главный культурный квест Санкт-Петербурга предлагает посетить десять знаковых объектов за один день. Игрокu доступна карта\\nмаршрута, расписание работы площадок и возможность бронировать билеты через внутриигровой интерфейс.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить маршрут и таймер марафона.\\n2. Пройти Эрмитаж и Исаакиевский собор, активируя AR-гайды.\\n3. Посетить Русский музей, Мариинский театр и Петропавловскую крепость.\\n4. Завершить оставшиеся точки: Казанский собор, Михайловский замок, Кунсткамеру и Стрелку Васильевского острова.\\n5. Сдать отчёт, подтверждающий посещение всех десяти объектов.\\n", "mechanics_links": [], "assets": []}, {"id": "time_management", "title": "Управление временем", "body": "У игрока есть 24 внутриигровых часа. Опоздание на закрытие одного из объектов требует закупки премиум-доступа или повтора маршрута.\\nПогода и транспорт влияют на скорость передвижения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 5 000 XP.\\n- Расходы: 500 еддис на билеты и транспорт.\\n- Атрибут «Культура»: +25, титул «Знаток Петербурга».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Тайм-челлендж, культурные отметки, фото-collectibles, рейтинги марафонцев.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Служит центральным событием культурной ветки Санкт-Петербурга и открывает доступ к VIP-экскурсиям в более поздних линиях.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 807, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Культурная столица» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::