INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2093-final-rave', 'Берлин 2078-2093 — Финальный рейв', 'Игрок участвует в синхронном клаб-крауле по всему Берлину, поддерживая энергию сцены до рассвета понедельника.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2093-final-rave", "title": "Берлин 2078-2093 — Финальный рейв", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "techno", "festival"], "topics": ["live-event", "endurance"], "related_systems": ["narrative-service", "liveops-service"], "related_documents": [{"id": "quest-berlin-2093-techno-legacy", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-043-final-rave.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальное техно-событие Берлина требовало формализации как марафонский live-event с наследием культуры.", "goal": "Описать 72-часовой рейв и его вклад в культурную линию города.", "essence": "Игрок участвует в синхронном клаб-крауле по всему Берлину, поддерживая энергию сцены до рассвета понедельника.", "key_points": ["Все клубы города работают одновременно в течение 72 часов.", "Выносливость и культурная вовлечённость игрока формируют бонусы.", "Событие служит кульминацией техно-культуры сезона."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Финальный рейв» запускает трёхдневный марафон танцев, объединяющий Berghain, Tresor, Watergate, Sisyphos и сотню других\\nклубов. Город превращается в единую сцену.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Старт в пятницу в 20:00 и открытие всех площадок.\\n2. Клаб-краул: посетить минимум десять клубов и собрать сет-листы DJ.\\n3. Поддерживать энергетику 72 часа без перерыва, используя баффы и поддержку коллектива.\\n4. Встречать рассвет понедельника на главной сцене и завершить марафон.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 10 000 XP.\\n- Финансы: -200 еддис на расходы (билеты, напитки).\\n- Ачивка «72-Hour Legend» и бонус к выносливости в новой лиге.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Улицы заполнены толпами, бас слышен в каждом районе, флаги клубов развеваются, а слоган «Never stop dancing» звучит повсюду.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Финальный рейв» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

