INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-summer-garden', 'Санкт-Петербург 2030-2039 — Летний сад', 'Игрок проходит по аллеям, взаимодействует с партнёром и открывает культурные приметы первого парка Петербурга.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-summer-garden", "title": "Санкт-Петербург 2030-2039 — Летний сад", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "romantic", "heritage"], "topics": ["romantic-encounter", "atmospheric-storytelling"], "related_systems": ["narrative-service", "social-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-016-summer-garden.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен спокойный романтический квест, подчеркивающий атмосферу исторического центра.", "goal": "Дать игрокам возможность провести свидание и изучить Летний сад с его статуями и историей.", "essence": "Игрок проходит по аллеям, взаимодействует с партнёром и открывает культурные приметы первого парка Петербурга.", "key_points": ["Фокус на эмоциях, визуальном ряде и исторических справках.", "Награда усиливает романтические отношения и впечатления.", "Локация служит передышкой между более напряжёнными миссиями."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Летний сад — первая парковая резиденция Петербурга. Квест предназначен для парных прогулок и культурных пауз. Игроки получают доступ к\\nинтерактивным табличкам со стихами и историей каждой статуи.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Пройти через знаменитую чугунную ограду сада.\\n2. Исследовать аллеи, активируя аудиогиды у ключевых статуй.\\n3. Посетить Летний дворец Петра I и изучить экспозицию о начале города.\\n4. Организовать романтический момент у центрального фонтана.\\n5. Завершить прогулку, оставив совместное фото в альбоме воспоминаний.\\n", "mechanics_links": [], "assets": []}, {"id": "emotional_content", "title": "Эмоциональный контент", "body": "Взаимодействия включают совместные диалоги, выбор подарков и возможность поделиться воспоминаниями. Партнёр реагирует на выбранные\\nмаршруты и комментарии игрока.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Мраморные статуи античных богов, лебединые пруды, тихая музыка камерных ансамблей и AR-экспозиции, рассказывающие о петровской эпохе.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 600 XP.\\n- Валюта: не требуется.\\n- Романтический атрибут: +10, ачивка «Прогулка Императора».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Социальные взаимодействия, мини-игры на подбор подарков, фото-collectibles.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с романтическими сюжетами Санкт-Петербурга и служит переходом к финальным социальным событиям сезона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 809, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Летний сад» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

