INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-berlin-2093-goodbye-berlin', 'Берлин 2078-2093 — Прощай, Берлин', 'Игрок посещает знаковые места, вспоминает историю города и завершает сезон личным прощанием.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-berlin-2093-goodbye-berlin", "title": "Берлин 2078-2093 — Прощай, Берлин", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "farewell", "tour"], "topics": ["social_event", "nostalgia"], "related_systems": ["narrative-service", "soundtrack-system"], "related_documents": [{"id": "quest-berlin-2020-brandenburg-intro", "relation": "mirrors"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-045-goodbye-berlin.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Прощальный тур по Берлину оставался в Markdown и не фиксировал эмоциональные точки маршрута.", "goal": "Описать дневной маршрут по культовым локациям с техно-саундтреком как финальное событие сезона.", "essence": "Игрок посещает знаковые места, вспоминает историю города и завершает сезон личным прощанием.", "key_points": ["Маршрут охватывает East Side Gallery, Museum Island, Berghain, Бранденбургские ворота и Рейхстаг.", "Постоянный техно-саундтрек связывает культурные слои и сцены города.", "Награды отражают эмоциональную ценность и память о Берлине."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "«Прощай, Берлин» — эмоциональный тур по пяти ключевым локациям, завершающий сезон города и подчеркивающий его культурную\\nидентичность.\\n", "mechanics_links": [], "assets": []}, {"id": "itinerary", "title": "Маршрут", "body": "1. Рассвет у East Side Gallery и осмотр граффити.\\n2. Дневная экскурсия на Museum Island и встреча с кураторами.\\n3. Вечерний сет в Berghain с друзьями по техно-сцене.\\n4. Ночная остановка у Бранденбургских ворот для совместного танца.\\n5. Рассвет на куполе Рейхстага и финальное селфи с видом на город.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Ачивка «Berliner Herz».\\n- Уникальный плейлист техно-треков и запись эмоций в личном журнале.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Наушники с техно-саундтреком, толпы туристов и жителей, суверенитет лозунга «Ich bin ein Berliner» на завершающей сцене.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован квест «Прощай, Берлин» в формат знаний Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

