-- Quest: quest-spb-2039-pavlovsk-palace
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2039-pavlovsk-palace', 'Санкт-Петербург 2030-2039 — Павловский дворец', 'Игрок исследует дворец, участвует в VR-реконструкции последних дней Павла I и сталкивается с ИИ-призраком монарха.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2039-pavlovsk-palace", "title": "Санкт-Петербург 2030-2039 — Павловский дворец", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "imperial-heritage", "mystery"], "topics": ["historical-investigation", "vr-reconstruction"], "related_systems": ["narrative-service", "vr-service", "exploration-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-017-pavlovsk-palace.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовался квест, соединяющий трагедию Павла I с современными технологиями реконструкции.", "goal": "Провести игрока по дворцово-парковому ансамблю, показав историю и мистику Павловска.", "essence": "Игрок исследует дворец, участвует в VR-реконструкции последних дней Павла I и сталкивается с ИИ-призраком монарха.", "key_points": ["Классицистская архитектура подчёркивает контраст с барокко других резиденций.", "VR-сцены показывают политические интриги и трагический финал.", "Призрак-ИИ задаёт вопросы о власти и долге игрока."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Павловск — строгий дворец на 600 гектарах парка. Квест сочетает экскурсионную часть с интерактивной реконструкцией событий 1801 года.\\nИгрок взаимодействует с гидом-андроидом и архивами Императорского дома.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Павловска и получить доступ к дворцу.\\n2. Осмотреть парадные залы, фиксируя архитектурные элементы классицизма.\\n3. Активировать VR-сценарий последних дней Павла I и наблюдать интриги двора.\\n4. Исследовать парк, собирая фрагменты памяти монарха.\\n5. Встретиться с ИИ-призраком Павла и ответить на его вопросы.\\n", "mechanics_links": [], "assets": []}, {"id": "mystery", "title": "Мистический элемент", "body": "Призрак-ИИ анализирует решения игрока, сравнивая их с поступками Павла I. Итоговая оценка влияет на культурную репутацию и выдаёт\\nуникальные заметки о судьбе императора.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Жёлтый фасад, строгие залы, коллекции антиквариата, парковые павильоны и реконструкция дворцовой спальни, где произошёл переворот.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 2 000 XP.\\n- Валюта: не начисляется.\\n- Атрибут «Культура»: +10 и запись в журнале «Тени Павловска».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "VR-реконструкция, расследование, опрос ИИ-призрака, сбор музейных артефактов.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Продолжает линию пригородных дворцов и подготавливает игрока к финальным мистическим квестам Санкт-Петербурга.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 810, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Павловский дворец» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

