INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-techno-utopia', 'Берлин 2040-2060 — «Техно-утопия»', 'Игрок вместе с командой захватывает квартал, вводит «голосование танцем» и защищает утопию от вмешательства государства.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-techno-utopia", "title": "Берлин 2040-2060 — «Техно-утопия»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "techno", "autonomy"], "topics": ["social-experiment", "faction-governance"], "related_systems": ["narrative-service", "reputation-system", "economy-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-022-techno-utopia.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не показывал управленческие решения техно-коммуны и конфликт с властями.", "goal": "Описать создание автономного района, где музыка становится политическим инструментом.", "essence": "Игрок вместе с командой захватывает квартал, вводит «голосование танцем» и защищает утопию от вмешательства государства.", "key_points": ["Организация инфраструктуры для круглосуточной музыки и коммунальных услуг.", "Коллективное принятие решений через рейвы и выбор ветки исходов.", "Последствия влияют на репутацию техно-линии и отношения с администрацией Берлина."]}, "content": {"sections": [{"id": "takeover", "title": "Захват района", "body": "Команда занимает заброшенный квартал, устанавливает звуковые системы и распределяет роли.\\nИгрок договаривается с соседями и оценивает риски полицейского вмешательства.\\n", "mechanics_links": [], "assets": []}, {"id": "governance", "title": "Музыкальное управление", "body": "Решения принимаются на рейвах, где голосование выражается интенсивностью танца.\\nСистема «Dance Democracy» обновляет показатели автономии и настроение жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "outcomes", "title": "Исходы утопии", "body": "- Утопия: район становится легальной культурной зоной и приносит экономический эффект.\\n- Подавление: власти разгоняют коммуну, открывая ветку сопротивления.\\n- Компромисс: подписывается меморандум о культурном статусе и правилах безопасности.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 10 000 XP, титул «Основатель техно-утопии», доступ к уникальным контрактам.\\n- Долгосрочные последствия: автономная зона или её ликвидация.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Dance Democracy", "definition": "Система голосования, где участники выражают выбор интенсивностью танца на рейве."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Техно-утопия» в knowledge-entry и описание управленческих исходов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EX