INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-saint-petersburg-2030-2039-ballet-premiere', 'Санкт-Петербург 2030-2039 — «Премьера балета»', 'Игрок проходит ускоренный путь балетной карьеры, от прослушивания до премьеры в Мариинском театре.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-saint-petersburg-2030-2039-ballet-premiere", "title": "Санкт-Петербург 2030-2039 — «Премьера балета»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "quest", "ballet"], "topics": ["artistic-career", "cultural-elite"], "related_systems": ["narrative-service", "performance-service", "reputation-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-018-ballet-premiere.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Карьерная линия культурного Петербурга не показывала путь к вершине Мариинского балета.", "goal": "Создать квест о становлении солистом, охватывающий прослушивание, репетиции и премьерный выход.", "essence": "Игрок проходит ускоренный путь балетной карьеры, от прослушивания до премьеры в Мариинском театре.", "key_points": ["Прослушивание и тренировки поддерживаются мини-играми и проверками навыков.", "Главная партия требует репетиций с легендарным хореографом.", "Премьера приносит славу и доступ к элитарным событиям."]}, "content": {"sections": [{"id": "audition", "title": "Прослушивание", "body": "Игрок участвует в строгом отборе, демонстрирует базовые элементы и убеждает художественный совет в своей уникальной пластике.\\n", "mechanics_links": [], "assets": []}, {"id": "training", "title": "Интенсивные тренировки", "body": "Мини-игры имитируют многолетние занятия у станка. Нужно поддерживать форму, избегать травм и работать с наставниками.\\n", "mechanics_links": [], "assets": []}, {"id": "lead_role", "title": "Получение главной партии", "body": "После серии успехов игроку доверяют роль в «Лебедином озере». Требуется набрать доверие маэстро и партнёров.\\n", "mechanics_links": [], "assets": []}, {"id": "rehearsal", "title": "Репетиции с хореографом", "body": "Игрок отрабатывает поддержку, па-де-де и финальные па, балансируя между творческой свободой и классическим каноном.\\n", "mechanics_links": [], "assets": []}, {"id": "premiere", "title": "Премьера в Мариинском театре", "body": "Заключительная сцена выводит игрока на сцену в голубых и золотых залах. Механика реакций публики и прессы определяет шлейф славы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP и ежемесячный доход 20 000 едди.\\n- Титул «Прима Мариинки» и доступ к культурной элите.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 811, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Премьера балета» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requ