INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-u-bahn-rave', 'Берлин 2040-2060 — «Рейв в U-Bahn»', 'Игрок организует подпольную вечеринку на Ringbahn, маневрирует между станциями и избегает ареста.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-u-bahn-rave", "title": "Берлин 2040-2060 — «Рейв в U-Bahn»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "metro", "techno"], "topics": ["mobile-event", "law-risk"], "related_systems": ["narrative-service", "event-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2020-2093", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-024-u-bahn-rave.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал мобильный формат рейва и риски столкновения с полицией.", "goal": "Зафиксировать сценарий захвата вагонов U-Bahn с установкой звуковой системы.", "essence": "Игрок организует подпольную вечеринку на Ringbahn, маневрирует между станциями и избегает ареста.", "key_points": ["Подготовка звукового оборудования в движущемся вагоне.", "Управление маршрутом и точками посадки участников.", "Скрытое противостояние с транспортной полицией и последствия для репутации."]}, "content": {"sections": [{"id": "setup", "title": "Подготовка платформы", "body": "Команда нелегально арендует вагон, устанавливает генераторы и тестирует звук.\\nИгрок распределяет роли: диджей, lookout, связь со станциями.\\n", "mechanics_links": [], "assets": []}, {"id": "ride", "title": "Рейв на кольцевой линии", "body": "Поезд движется по Ringbahn, пассажиры запрыгивают на станциях по сигналам в соцсетях.\\nИгрок балансирует громкость и скрытность, чтобы не привлечь ранний рейд полиции.\\n", "mechanics_links": [], "assets": []}, {"id": "police", "title": "Противостояние", "body": "Полиция пытается остановить вагон; игрок выбирает — уйти в тоннель, договориться или устроить финальный сет.\\nИсход определяет репутацию и возможный арест.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 4 000 XP, +20 к репутации техно-сцены.\\n- Риск ареста снижает репутацию у властей, но даёт ачивку «Техно-поезд».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Ringbahn", "definition": "Кольцевая линия берлинского метро, идеально подходящая для непрерывного рейва в движении."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Рейв в U-Bahn» в knowledge-entry и описание мобильной вечеринки."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

