INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2078-2093-unity-techno', 'Берлин 2078-2093 — «Техно Единства»', 'Игрок координирует техно-парад из East Side Gallery к Бранденбургским воротам и запускает обратный отсчёт до новой эпохи.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2078-2093-unity-techno", "title": "Берлин 2078-2093 — «Техно Единства»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "parade"], "topics": ["city_quests", "seasonal_event"], "related_systems": ["narrative-service", "event-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-047-unity-techno.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Перед перезапуском лиги нужен сюжетный парад, подчёркивающий объединение восточного и западного Берлина.", "goal": "Организовать финальный техно-марш вдоль бывшей стены и объединить игроков вокруг символов свободы.", "essence": "Игрок координирует техно-парад из East Side Gallery к Бранденбургским воротам и запускает обратный отсчёт до новой эпохи.", "key_points": ["Марш проходит по трассе бывшей стены и соединяет ключевые исторические точки.", "Музыкальный формат подчеркивает роль техно-культуры в идентичности Берлина.", "Обратный отсчёт переводит город в финальную фазу перед «Вечным Техно»."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Квест открывается после культурных задач города. Городской совет просит игрока стать лицом парада,\\nобъединяющего жителей и гостей Берлина на последнем танцевальном маршруте лиги.\\n", "mechanics_links": [], "assets": []}, {"id": "route", "title": "Маршрут и этапы", "body": "1. Сбор у East Side Gallery и настройка звуковых платформ.\\n2. Передвижение колонны вдоль 13-километрового маршрута бывшей стены.\\n3. Организация остановок у Checkpoint Charlie, Potsdamer Platz и мемориалов.\\n4. Финал у Бранденбургских ворот с синхронными световыми эффектами.\\n5. Запуск обратного отсчёта до перезапуска лиги и общегородской трансляции.\\n", "mechanics_links": [], "assets": []}, {"id": "experience", "title": "Игровой опыт", "body": "Игрок управляет миксом в реальном времени, реагирует на настроение толпы и активирует AR-хроники,\\nчтобы показать путь от разделённого к единому Берлину. Погода и толпы влияют на темпо-ритм и корректировки маршрута.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Награда включает 5,000 XP, ачивку «Техно-Освободитель» и память о параде в виде уникального саундпака.\\nФинансовые бонусы отсутствуют, чтобы подчеркнуть идею единства и свободы.\\n", "mechanics_links": [], "assets": []}, {"id": "linkage", "title": "Связи", "body": "Событие ведёт непосредственно к квесту «Вечное Техно» и зависит от выполнения культурных задач Берлина.\\nРекомендуется после завершения «Символа Медведя» и перед финальным эпилогом города.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Техно Единства» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

