INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-saint-petersburg-2030-2039-rostral-columns', 'Санкт-Петербург 2030-2039 — «Ростральные колонны»', 'Игрок получает поручение зажечь колонны, выполняет подготовку и наблюдает празднование города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-saint-petersburg-2030-2039-rostral-columns", "title": "Санкт-Петербург 2030-2039 — «Ростральные колонны»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "quest", "ceremony"], "topics": ["civic-events", "heritage-rituals"], "related_systems": ["narrative-service", "liveops-events-service", "reputation-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-019-rostral-columns.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Праздничные ритуалы Петербурга не описывали церемонию зажигания огня на Ростральных колоннах.", "goal": "Создать квест о подготовке и проведении огненной церемонии на Стрелке Васильевского острова.", "essence": "Игрок получает поручение зажечь колонны, выполняет подготовку и наблюдает празднование города.", "key_points": ["Колонны служат маяками и символом морской славы.", "Церемония требует точности и соблюдения протокола.", "Зажжённый огонь запускает городской праздник."]}, "content": {"sections": [{"id": "assignment", "title": "Поручение городской думы", "body": "Игрок получает официальный орден, изучает историю колонн и проходит инструктаж по технике безопасности перед подъёмом.\\n", "mechanics_links": [], "assets": []}, {"id": "ascent", "title": "Подъём на колонну", "body": "Нужно преодолеть 32-метровую лестницу, проверяя оборудование и связь с дирижёром церемонии. Ветер и морской туман усложняют подъём.\\n", "mechanics_links": [], "assets": []}, {"id": "ignition", "title": "Зажжение факела", "body": "Игрок синхронизирует запуск огня с музыкальным сигналом. Вариативность успеха влияет на панораму и реакцию толпы.\\n", "mechanics_links": [], "assets": []}, {"id": "celebration", "title": "Праздничный финал", "body": "После зажигания город запускает фейерверк, а игрок может наблюдать панораму Невы и разделить момент со зрителями.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 200 XP, 5 000 едди и репутация города +5.\\n- Ачивка «Хранитель огня» и приглашения на будущие церемонии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 812, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Ростральные колонны» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

