INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-saint-petersburg-2030-2039-imperial-ball', 'Санкт-Петербург 2030-2039 — «Императорский бал»', 'Игрок добывает приглашение, готовит наряд, учится танцам и участвует в кульминационном вальсе.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-saint-petersburg-2030-2039-imperial-ball", "title": "Санкт-Петербург 2030-2039 — «Императорский бал»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "quest", "imperial-ball"], "topics": ["social-elite", "etiquette-gameplay"], "related_systems": ["narrative-service", "social-service", "liveops-events-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2030-2039/quest-020-imperial-ball.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Элитные события Петербурга отсутствовали в виде подробного игрового сценария императорского бала.", "goal": "Подготовить квест о визите на бал в Зимнем дворце с изучением этикета и танцев.", "essence": "Игрок добывает приглашение, готовит наряд, учится танцам и участвует в кульминационном вальсе.", "key_points": ["Приглашение требует высокую репутацию и подготовку костюма.", "Этикет и танцы реализованы через мини-игры.", "Бал открывает доступ к элитным связям и интригам."]}, "content": {"sections": [{"id": "invitation", "title": "Получение приглашения", "body": "Нужно добиться репутации элиты, пройти проверку службы двора и оплатить пошлину за участие. Игрок знакомится с протоколом балов.\\n", "mechanics_links": [], "assets": []}, {"id": "preparation", "title": "Подготовка наряда и обучения", "body": "Игрок заказывает платье или фрак, посещает уроки этикета и изучает полонез, мазурку и вальс через танцевальные мини-игры.\\n", "mechanics_links": [], "assets": []}, {"id": "ballroom", "title": "Бал в Зимнем дворце", "body": "Сцена с мраморными залами, оркестром и приветствиями. Игрок взаимодействует с гостями, выбирает диалоги и участвует в интригах.\\n", "mechanics_links": [], "assets": []}, {"id": "climax", "title": "Кульминационный вальс", "body": "Финальный танец в Николаевском зале. Успех зависит от точности выполнения движений и соблюдения этикета.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP и титул «Фрейлина» или «Камергер».\\n- Репутация элиты +10 и доступ к закрытым салонам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 813, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Императорский бал» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

