INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-abandoned-factory', 'Берлин 2040-2060 — «Заброшенная фабрика»', 'Игрок находит фабрику, сквотирует, переоборудует и запускает культовую площадку.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-abandoned-factory", "title": "Берлин 2040-2060 — «Заброшенная фабрика»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "club", "diy"], "topics": ["construction", "nightlife"], "related_systems": ["narrative-service", "economy-system", "reputation-system"], "related_documents": [{"id": "canon-region-europe-berlin-2040-2060", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-026-abandoned-factory.md", "visibility": "internal", "audience": ["narrative", "quest-design", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал процесс превращения фабрики в клуб и вызовы DIY-культуры.", "goal": "Структурировать строительство нового техно-клуба на основе заброшенной индустриальной площадки.", "essence": "Игрок находит фабрику, сквотирует, переоборудует и запускает культовую площадку.", "key_points": [{"Промышленная эстетика": "бетон и сталь."}, {"Инфраструктура клуба": "звук, бар, darkroom, chill-out."}, "Фейс-контроль и позиционирование в техно-сцене."]}, "content": {"sections": [{"id": "scouting", "title": "Поиск и захват", "body": "Игрок исследует индустриальные районы и выбирает фабрику с нужной акустикой.\\nСквот-команда захватывает объект, обеспечивая охрану и доступ.\\n", "mechanics_links": [], "assets": []}, {"id": "renovation", "title": "Переоборудование", "body": "- Устанавливается звуковая система, бар, darkroom и зоны отдыха.\\n- Мини-игры по монтажу света и вентиляции влияют на качество клуба.\\n", "mechanics_links": [], "assets": []}, {"id": "launch", "title": "Первая вечеринка", "body": "Игрок приглашает известных DJ, проводит фейс-контроль и контролирует атмосферу.\\nИсход определяет репутацию и отзывы сцены.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, пассивный доход 50 000 кредов в неделю, репутация «Легенда техно-сцены».\\n- Доступ к VIP-коллаборациям и эксклюзивным артистам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "DIY club", "definition": "Клуб, построенный своими силами в заброшенном индустриальном здании, продолжая традицию Berghain."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Заброшенная фабрика» в knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

