-- Quest: canon-quest-berlin-2078-2093-last-currywurst
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2078-2093-last-currywurst', 'Берлин 2078-2093 — «Последний Currywurst»', 'Игрок возвращается к знакомой будке, чтобы разделить последнюю порцию currywurst с владельцем и проститься с городом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2078-2093-last-currywurst", "title": "Берлин 2078-2093 — «Последний Currywurst»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "culinary"], "topics": ["city_quests", "slice_of_life"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-049-last-currywurst.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальный акт берлинской гастрономической линии должен вызвать ностальгию и связать ранние квесты про currywurst.", "goal": "Закрепить сюжетную арку уличной еды, показав как традиция переживает конец лиги.", "essence": "Игрок возвращается к знакомой будке, чтобы разделить последнюю порцию currywurst с владельцем и проститься с городом.", "key_points": ["Сценарий опирается на ранние задания (quest-006 и quest-012) и завершает кулинарную ветку.", "Простая сцена питания превращается в эмоциональное прощание с Берлином.", "Награда не только в XP, но и в закреплении статуса «Душа Currywurst»."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Активируется перед финальным квестом города. Игрок получает сообщение от владельца будки,\\nкоторый просит заглянуть «ещё раз перед перезапуском». Локация та же, что и в первых миссиях линии.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Этапы прохождения", "body": "1. Прибыть к будке currywurst и узнать новости у владельца.\\n2. Заказать классический набор с pommes и наблюдать подготовку.\\n3. Съесть блюдо, вспоминая ключевые моменты берлинской арки.\\n4. Обсудить будущее будки и города, получить завершающую реплику «Ein letztes Mal».\\n5. Завершить сцену благодарностью и обновлённым статусом.\\n", "mechanics_links": [], "assets": []}, {"id": "flavor", "title": "Детали и настроение", "body": "Акцент на визуале красного соуса, одноразовой вилки и слёз владельца.\\nМузыка приглушённая, слышны отголоски техно-сцен. Камера фиксирует деталей,\\nподчёркивая, что маленькие традиции столь же важны, как глобальные события.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Игрок получает 800 XP, титул «Душа Currywurst» и небольшое снижение валюты (-5 единиц).\\nНаграда подчёркивает эмоциональную ценность поступка, а не материальную выгоду.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связанные линии", "body": "Квест замыкает сюжетную дугу уличной еды Берлина, завершая цепочку,\\nначатую в quest-006 и продолженную в quest-012. Рекомендуется проходить перед «Вечным Техно».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Последний Currywurst» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

