INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-saint-petersburg-2040-2060-canal-races', 'Санкт-Петербург 2040-2060 — «Гонки по каналам»', 'Игрок модифицирует гидроцикл, участвует в гонке по пяти каналам и борется за легендарный трофей.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-saint-petersburg-2040-2060-canal-races", "title": "Санкт-Петербург 2040-2060 — «Гонки по каналам»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "quest", "hydro-race"], "topics": ["racing-events", "water-sports"], "related_systems": ["narrative-service", "racing-service", "leaderboard-service"], "related_documents": [{"id": "timeline-author-quests-index", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-022-canal-races.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не было современного гоночного события, использующего затопленные каналы Петербурга.", "goal": "Сконструировать гонку на гидроциклах с уникальной трассой и препятствиями под мостами.", "essence": "Игрок модифицирует гидроцикл, участвует в гонке по пяти каналам и борется за легендарный трофей.", "key_points": ["Трасса проходит через Неву, Мойку, канал Грибоедова, Фонтанку и Крюков канал.", "Низкие мосты и гондолы создают динамичные препятствия.", "Соревнование входит в регулярные водные мероприятия города."]}, "content": {"sections": [{"id": "registration", "title": "Регистрация и подготовка", "body": "Игрок оплачивает вступительный взнос 2 000 едди, выбирает класс гидроцикла и устанавливает модификации для поворотливости и ускорения.\\n", "mechanics_links": [], "assets": []}, {"id": "race", "title": "Гонка по каналам", "body": "Соревнование начинается на Неве и продолжается по узким каналам. Игрок должен наклоняться под мостами и избегать столкновений с\\nгондолами.\\n", "mechanics_links": [], "assets": []}, {"id": "obstacles", "title": "Препятствия трассы", "body": "Гонки сопровождаются погодными эффектами, переменными течениями и неожиданными баржами. Дополнительные мини-ивенты дают бонусы скорости.\\n", "mechanics_links": [], "assets": []}, {"id": "finish", "title": "Финиш у Адмиралтейства", "body": "Финальный участок требует точного манёвра между статуями и мостами. От времени прохождения зависит награда.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 место: 25 000 едди и легендарный гидроцикл «Канальный гонщик».\\n- Участие: 1 500 XP и приглашение на еженедельные события.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 815, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_team", "changes": "Конвертация квеста «Гонки по каналам» в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP