-- Quest: canon-quest-berlin-2061-2077-tech-conference
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2061-2077-tech-conference', 'Берлин 2061-2077 — «Берлинская Tech-конференция»', 'Игрок выступает на техносаммите, расширяет сеть контактов и закрепляет репутацию города как европейского тех-хаба.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2061-2077-tech-conference", "title": "Берлин 2061-2077 — «Берлинская Tech-конференция»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "technology"], "topics": ["city_quests", "events"], "related_systems": ["narrative-service", "economy-service", "social-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2061-2077/quest-036-berlin-tech-conference.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Берлину нужна крупная сцена для демонстрации технологического лидерства и привлечения инвестиций в локальные стартапы.", "goal": "Организовать участие игрока в Berlin Tech Summit с фокусом на питче проекта, нетворкинге и интеграции в инновационные экосистемы.", "essence": "Игрок выступает на техносаммите, расширяет сеть контактов и закрепляет репутацию города как европейского тех-хаба.", "key_points": ["Messe Berlin превращается в центр европейских инноваций и возможностей для стартапов.", "Игрок получает уникальные инвестиционные договорённости через механики нетворкинга.", "After-party на техно-сцене усиливает культурную идентичность квеста."]}, "content": {"sections": [{"id": "registration", "title": "Регистрация и подготовка", "body": "Игрок оформляет участие на Berlin Tech Summit, получает бейдж и доступ в закрытые зоны.\\nНа этапе подготовки требуется собрать презентационный комплект, выбрать целевых инвесторов\\nи согласовать график выступлений.\\n", "mechanics_links": [], "assets": []}, {"id": "pitch", "title": "Презентация проекта", "body": "Главная сцена форума предоставляет слот для питча. Игрок должен адаптировать презентацию под аудиторию,\\nотвечать на вопросы инвесторов и демонстрировать работающий прототип.\\nУспех открывает дополнительные предложения и пресс-интерес.\\n", "mechanics_links": [], "assets": []}, {"id": "networking", "title": "Нетворкинг и сделки", "body": "В VIP-зонах Messe Berlin игрок проводит серию встреч с инвесторами, корпорациями и другими стартапами.\\nВыбор партнёров влияет на будущие задания, доступ к капиталу и на социальные связи.\\n", "mechanics_links": [], "assets": []}, {"id": "demos", "title": "Демо-зоны инноваций", "body": "В павильонах AI, биотеха и квантовых вычислений игрок тестирует решения партнёров, собирает данные\\nи может подписать соглашения об интеграции технологий.\\n", "mechanics_links": [], "assets": []}, {"id": "afterparty", "title": "Техно After-party", "body": "Завершение происходит на фирменной техно-вечеринке. Игрок закрепляет связи, получает неформальную обратную связь\\nи может открыть дополнительные поручения от клубной сцены Берлина.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Берлинская Tech-конференция» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

