INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2060-neva-pirates', 'Санкт-Петербург 2040-2060 — Пираты Невы', 'Игрок проходит испытания банды, грабит караван и участвует в водном рейде под разводными мостами.', 'side', 8, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "meet_pirate_captain", "text": "Встретиться с капитаном пиратской гильдии на Стрелке Васильевского острова, изучить устав и принять кодекс пиратов", "type": "interact", "target": "neva_pirates_captain_meeting", "count": 1, "optional": false}, {"id": "intercept_smuggling_convoy", "text": "Выполнить испытание — перехватить контрабандный караван на Неве", "type": "perform", "target": "neva_smuggling_convoy_intercept", "count": 1, "optional": false}, {"id": "naval_combat_under_bridges", "text": "Провести водный бой под разводными мостами, используя тактику укрытий, артиллерию и дронов", "type": "perform", "target": "neva_naval_combat_bridges", "count": 1, "optional": false}, {"id": "pirate_initiation", "text": "Принести трофеи в пиратскую гильдию и пройти церемонию посвящения", "type": "interact", "target": "neva_pirates_initiation", "count": 1, "optional": false}, {"id": "receive_pirate_ship", "text": "Получить собственный модульный корабль и доступ к пиратским контрактам", "type": "interact", "target": "neva_pirates_ship_reward", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "currency": 15000, "items": [], "reputation": {"Neva_Pirates": 50}, "flags": ["neva_pirates_guild_access", "neva_pirates_ship_unlock"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2060-neva-pirates", "title": "Санкт-Петербург 2040-2060 — Пираты Невы", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-01T21:51:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "piracy", "river-combat"], "topics": ["faction-gameplay", "naval-combat"], "related_systems": ["narrative-service", "faction-service", "naval-combat-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-024-neva-pirates.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалось показать криминальный контроль водных путей Петербурга в 2040-х.", "goal": "Позволить игроку вступить в пиратскую гильдию Невы и получить собственное судно.", "essence": "Игрок проходит испытания банды, грабит караван и участвует в водном рейде под разводными мостами.", "key_points": ["Пиратская гильдия оформлена в стиле петровских фрегатов с киберпанк начинкой.", "Речной бой комбинирует артиллерию, дронов и манёвры под мостами.", "Полученный корабль открывает ветку пиратских контрактов."]}, "quest_definition": {"quest_type": "side", "level_min": 8, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "meet_pirate_captain", "text": "Встретиться с капитаном пиратской гильдии на Стрелке Васильевского острова, изучить устав и принять кодекс пиратов", "type": "interact", "target": "neva_pirates_captain_meeting", "count": 1, "optional": false}, {"id": "intercept_smuggling_convoy", "text": "Выполнить испытание — перехватить контрабандный караван на Неве", "type": "perform", "target": "neva_smuggling_convoy_intercept", "count": 1, "optional": false}, {"id": "naval_combat_under_bridges", "text": "Провести водный бой под разводными мостами, используя тактику укрытий, артиллерию и дронов", "type": "perform", "target": "neva_naval_combat_bridges", "count": 1, "optional": false}, {"id": "pirate_initiation", "text": "Принести трофеи в пиратскую гильдию и пройти церемонию посвящения", "type": "interact", "target": "neva_pirates_initiation", "count": 1, "optional": false}, {"id": "receive_pirate_ship", "text": "Получить собственный модульный корабль и доступ к пиратским контрактам", "type": "interact", "target": "neva_pirates_ship_reward", "count": 1, "optional": false}], "rewards": {"experience": 3000, "currency": 15000, "items": [], "reputation": {"Neva_Pirates": 50}, "flags": ["neva_pirates_guild_access", "neva_pirates_ship_unlock"]}, "branches": []}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Пиратская банда контролирует торговлю Невой. Для вступления игрок встречается с капитаном на Стрелке, изучает устав и принимает\\nкодекс пиратов. Параллельно отслеживаются репутация и отношение городских властей.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с капитаном на Стрелке Васильевского острова.\\n2. Выполнить испытание — перехватить контрабандный караван на Неве.\\n3. Провести водный бой под разводными мостами, используя тактику укрытий.\\n4. Принести трофеи в пиратскую гильдию и пройти церемонию посвящения.\\n5. Получить собственный модульный корабль и доступ к пиратским контрактам.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Корабли стилизованы под петровские фрегаты, Андреевский флаг дополнен символом черепа, а бой сопровождают разводы мостов и\\nголографические проекции морской славы. Маршрут идёт от Финского залива до Малой Невы.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 3 000 XP.\\n- Валюта: 15 000 еддис и доля в пиратских прибылях.\\n- Доступ к пиратской гильдии и персональному кораблю.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Водные сражения, система фракций, контрабандные маршруты, рейдовые контракты.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Связан с международной линией пиратов (Лондон, Токио) и влияет на экономику водных путей региона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 817, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-01", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики водных сражений и пиратской гильдии."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Пираты Невы» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

