INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-techno-cathedral', 'Берлин 2040-2060 — «Техно-собор»', 'Игрок реконструирует заброшенную церковь, устраивает 24-часовой рейв и объявляет техно новой религией.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-techno-cathedral", "title": "Берлин 2040-2060 — «Техно-собор»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "techno", "architecture"], "topics": ["construction", "philosophy"], "related_systems": ["narrative-service", "reputation-system", "event-system"], "related_documents": [{"id": "canon-region-europe-berlin-2040-2060", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2040-2060/quest-030-techno-cathedral.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не описывал трансформацию церкви и философию «техно как религия».", "goal": "Структурировать строительство техно-собора с raid-событием и духовным подтекстом.", "essence": "Игрок реконструирует заброшенную церковь, устраивает 24-часовой рейв и объявляет техно новой религией.", "key_points": ["Готическая архитектура, витражи и лазер-шоу.", "DJ-проповеди и философия «Techno ist unsere Religion».", "Массовое событие (raid 10+) с долгосрочными последствиями."]}, "content": {"sections": [{"id": "restoration", "title": "Реставрация церкви", "body": "Игрок оценивает конструкцию, устанавливает звук в алтаре и усиливает своды для басов.\\nЛазеры проходят через витражи, создавая мистическое освещение.\\n", "mechanics_links": [], "assets": []}, {"id": "service", "title": "Техно-служба", "body": "- 24-часовой рейв с проповедями DJ.\\n- Игрок определяет ритуалы и философию собора.\\n- Молитвы заменены на синтезаторные мантры и общие импровизации.\\n", "mechanics_links": [], "assets": []}, {"id": "aftermath", "title": "Последствия", "body": "Событие привлекает медиа, городские власти и паломников.\\nВыбор: сохранить автономию или легализоваться как культурный объект.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 15 000 XP, титул «Первосвященник техно».\\n- Событие «Techno Mass» становится доступным раз в сезон.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Techno Mass", "definition": "Техно-служба с DJ-проповедями, символизирующая веру в музыку как духовную практику."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Техно-собор» в knowledge-entry и описание религиозной философии."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

