INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-brussels-2020-2029-belgian-chocolate', 'Брюссель 2020-2029 — Бельгийский шоколад', 'Игрок погружается в мировую столицу шоколада, дегустируя pralines и узнавая бренды Neuhaus, Leonidas и Godiva.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-brussels-2020-2029-belgian-chocolate", "title": "Брюссель 2020-2029 — Бельгийский шоколад", "document_type": "canon", "category": "quest", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T02:25:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "lore_analyst", "contact": "lore@necp.game"}], "tags": ["europe", "brussels", "quest"], "topics": ["timeline-author", "culinary-tour"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "canon-region-europe-brussels-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/brussels/2020-2029/quest-003-belgian-chocolate.md", "visibility": "internal", "audience": ["lore", "narrative", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "lore_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест дегустации бельгийского шоколада оставался неструктурированным и не отражал культурные детали pralines.", "goal": "Описать маршрут по шоколадным бутикам Брюсселя в YAML, выделив брендовые точки и историю pralines.", "essence": "Игрок погружается в мировую столицу шоколада, дегустируя pralines и узнавая бренды Neuhaus, Leonidas и Godiva.", "key_points": ["Grand Place окружена множеством магазинов, предлагающих 2000+ шоколадных вариантов.", "История pralines 1912 года от Neuhaus и статус Брюсселя как шоколадной столицы.", "Квест усиливает кулинарную линию и экономику роскоши."]}, "content": {"sections": [{"id": "overview", "title": "Контекст", "body": "Брюссель известен как столица шоколада благодаря инновациям pralines и множеству бутиков.\\nКвест подчёркивает гастрономическую идентичность города и его экономику подарков премиум-класса.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-steps", "title": "Этапы", "body": "1. Посетить магазины на Grand Place и изучить асортимент pralines.\\n2. Продегустировать разные виды шоколада: тёмный, молочный, белый, трюфели.\\n3. Зайти в Neuhaus и узнать об изобретении pralines в 1912 году.\\n4. Приобрести фирменную коробку и оценить ценовую политику.\\n5. Зафиксировать статус Брюсселя как шоколадной столицы мира.\\n", "mechanics_links": [], "assets": []}, {"id": "regional-details", "title": "Региональные детали", "body": "Указывается наличие более 2000 магазинов, традиции упаковки и бренды Leonidas, Neuhaus, Godiva.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Опыт: 1000 XP.\\nВалюта: −30.\\nБафф: +20% Energy на 6 часов.\\nАчивка: «Шоколадный гурман».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Кулинарные, дегустационные и роскошные механики стимулируют социальные и экономические сценарии.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 56, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Бельгийский шоколад» в YAML и описание гастрономических механик."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

