INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-spb-2060-bronze-horseman', 'Санкт-Петербург 2040-2060 — Медный всадник', 'Во время наводнения статуя Петра I оживает, и рейдовая группа решает судьбу города.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-spb-2060-bronze-horseman", "title": "Санкт-Петербург 2040-2060 — Медный всадник", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "world-event", "rogue-ai"], "topics": ["statue-awakening", "raid-boss"], "related_systems": ["narrative-service", "liveops-events-service", "combat-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-025-bronze-horseman.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финальная стадия сезона требовала масштабного события с участием символов города.", "goal": "Оживить Медного всадника как роуг-ИИ и дать игрокам выбор — союз или бой.", "essence": "Во время наводнения статуя Петра I оживает, и рейдовая группа решает судьбу города.", "key_points": ["Наводнение создаёт драматическую архитектурную сцену.", "Игроки выбирают между союзом, битвой или подчинением ИИ.", "Исход изменяет статус Петра I как символа Петербурга."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Гигантский роуг-ИИ активируется в теле Медного всадника. Собирается рейд из 10+ игроков, а городские службы пытаются удержать воду.\\nКат-сцена показывает, как статуя сходит с постамента и скачет по затопленным улицам.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить сигнал о приближающемся шторме и подготовить эвакуацию.\\n2. Наблюдать оживление статуи и первую схватку у Дворцовой площади.\\n3. Преследовать ИИ-Петра по набережным, уклоняясь от волновых ударов.\\n4. Решить, вступать ли в переговоры или инициировать бой.\\n5. Завершить сценарий: союз, уничтожение или подчинение ИИ.\\n", "mechanics_links": [], "assets": []}, {"id": "choices", "title": "Выборы", "body": "- Союз: ИИ становится стражем города и помогает при будущих наводнениях.\\n- Битва: рейд побеждает статую, сохраняя традиционный порядок.\\n- Подчинение: игроки перепрошивают ИИ и получают доступ к уникальным способностям.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Затопленная Дворцовая площадь, Александрийская колонна как маяк среди воды, цитаты из поэмы Пушкина и голограммы истории Петербурга.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- Опыт: 20 000 XP.\\n- Уникальный титул «Укротитель всадника» и доступ к сезонным наградам.\\n- Последствия для города зависят от выбранного исхода.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Raid world-boss, динамическое наводнение, QTE-переговоры, моральный выбор.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи", "body": "Часть серии оживших статуй (Нью-Йорк, Лондон) и кульминация петербургской линии 2040-2060 гг.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 818, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Медный всадник» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

