INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2040-2060-moika-romance', 'Санкт-Петербург 2040-2060 — «Романс на Мойке»', 'Игрок приглашает партнёра, арендует гондолу и переживает набор романтических сцен вдоль Мойки.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2040-2060-moika-romance", "title": "Санкт-Петербург 2040-2060 — «Романс на Мойке»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "romance", "canal"], "topics": ["social-encounter", "atmospheric-event"], "related_systems": ["narrative-service", "social-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-026-moika-romance.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Романтической ветке Петербурга нужен водный сценарий с камерной эстетикой канала Мойки.", "goal": "Создать атмосферную прогулку на гондоле, усиливающую показатели отношений и иммерсию.", "essence": "Игрок приглашает партнёра, арендует гондолу и переживает набор романтических сцен вдоль Мойки.", "key_points": ["Мойка и Зимняя канавка задают узкие живописные декорации.", "Львиный мост становится кульминационным моментом.", "Квест усиливает систему романтических бонусов и эстетических эмоций."]}, "content": {"sections": [{"id": "invitation", "title": "Приглашение и подготовка", "body": "Игрок выбирает партнёра, бронирует вечерний маршрут у гондольера и подбирает подарки, влияющие на романтический отклик.\\n", "mechanics_links": ["mechanics/social/relationship-invite.yaml"], "assets": []}, {"id": "cruise", "title": "Прогулка на гондоле", "body": "Путешествие по каналу Мойки с вокальными выступлениями гондольера, рассказами о дворцах и выбором вариантов диалога.\\n", "mechanics_links": ["mechanics/narrative/dialog-choice.yaml"], "assets": []}, {"id": "highlight", "title": "Мосты и кульминация", "body": "Проход под Львиным мостом с интерактивной сценой, возможность сделать предложение или обменяться клятвами.\\n", "mechanics_links": ["mechanics/social/signature-moment.yaml"], "assets": []}, {"id": "finale", "title": "Финал у Зимней канавки", "body": "Завершение прогулки у Зимней канавки, фиксация эмоций, получение баффа «Романтика Мойки» и открытие новых дейтов.\\n", "mechanics_links": ["mechanics/progression/emotion-buff.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 000 XP, Романс +20, траты 5 000 еддис.\\n- Ачивка «Романтика Мойки».\\n", "mechanics_links": ["mechanics/progression/achievement-grant.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 819, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Романс на Мойке» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURR