INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2061-2077-biotech-lab', 'Берлин 2061-2077 — «Биотех лаборатория»', 'Квест сочетает научные исследования, тестирование на добровольцах и моральный выбор о судьбе технологии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2061-2077-biotech-lab", "title": "Берлин 2061-2077 — «Биотех лаборатория»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "biotech"], "topics": ["city_quests", "implants"], "related_systems": ["narrative-service", "research-service", "medical-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2061-2077/quest-039-biotech-lab.md", "visibility": "internal", "audience": ["concept", "narrative", "medical"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Берлин стремится удержать лидерство в области биотехнологий и имплантов, требуя новых этически устойчивых решений.", "goal": "Погрузить игрока в работу биотех-стартапа, разработку нейроимпланта и принятие решений о его распространении.", "essence": "Квест сочетает научные исследования, тестирование на добровольцах и моральный выбор о судьбе технологии.", "key_points": ["Игрок получает доступ к уникальным имплантам с продвинутыми механиками.", "Этические дилеммы формируют репутацию фракций и персональные последствия.", "Берлин укрепляет статус медицинского инновационного центра."]}, "content": {"sections": [{"id": "hiring", "title": "Вступление в стартап", "body": "Игрок проходит собеседование, подтверждает компетенции и знакомится с командой биотехников.\\nНеобходимо распределить роли, определить финансирование и защитить лабораторию от корпоративного шпионажа.\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Разработка нейроимпланта", "body": "Этап охватывает прототипирование, калибровку интерфейсов мозг-машина и интеграцию с существующими имплантами.\\nИгрок решает технические проблемы, собирает биометрические данные и управляет производством.\\n", "mechanics_links": [], "assets": []}, {"id": "testing", "title": "Тестирование на добровольцах", "body": "Игрок организует клинические испытания, обеспечивает безопасность подопечных и реагирует на непредвиденные эффекты.\\nРешения влияют на доверие общества и последующие задания.\\n", "mechanics_links": [], "assets": []}, {"id": "breakthrough", "title": "Прорыв и сертификация", "body": "Нейроимплант демонстрирует стабильность. Игрок оформляет патенты, добивается сертификации\\nи готовит презентацию для медицинских форумов.\\n", "mechanics_links": [], "assets": []}, {"id": "distribution", "title": "Стратегия распространения", "body": "На финальном этапе игрок выбирает путь: продать технологию корпорации, сделать её открытой или создать монополию.\\nВыбор меняет доступность импланта, экономику региона и отношение фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Биотех лаборатория» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

