INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2040-2060-griboedov-canal', 'Санкт-Петербург 2040-2060 — «Канал Грибоедова»', 'Игрок отправляется в тур, посещает Казанский собор, мосты с грифонами и завершает маршрут у Спаса на Крови.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2040-2060-griboedov-canal", "title": "Санкт-Петербург 2040-2060 — «Канал Грибоедова»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "canal-tour", "culture"], "topics": ["exploration", "photography"], "related_systems": ["narrative-service", "exploration-service", "social-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-027-griboedov-canal.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требовалась прогулочная миссия, раскрывающая эстетику канала Грибоедова и его знаковые мосты.", "goal": "Провести игрока через серию остановок вдоль канала, сочетая экскурсию, фотографию и исторические заметки.", "essence": "Игрок отправляется в тур, посещает Казанский собор, мосты с грифонами и завершает маршрут у Спаса на Крови.", "key_points": ["Канал Грибоедова — извилистый маршрут с уникальными мостами и архитектурой.", "Фотосессии поощряют исследование и подбор ракурсов.", "Квест усиливает туристическую линию и эстетические показатели."]}, "content": {"sections": [{"id": "start", "title": "Старт у Казанского собора", "body": "Игрок получает маршрут, аудиогид и контрольный список кадров. Экскурсовод рассказывает о истории канала.\\n", "mechanics_links": ["mechanics/exploration/guided-tour.yaml"], "assets": []}, {"id": "bridges", "title": "Мосты и остановки", "body": "Маршрут включает Банковский мост с грифонами, Львиный мост и Итальянский мост. У каждой точки есть мини-сцена и бонус за фото.\\n", "mechanics_links": ["mechanics/exploration/photo-checkpoints.yaml"], "assets": []}, {"id": "finale", "title": "Финал у Спаса на Крови", "body": "Завершение у храма, обзор куполов в отражении воды, финальный снимок и получение эстетического бонуса.\\n", "mechanics_links": ["mechanics/exploration/reflection-moment.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1 200 XP, эстетика +10.\\n- Ачивка «Хранитель каналов».\\n", "mechanics_links": ["mechanics/progression/achievement-grant.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 820, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Канал Грибоедова» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

