INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2040-2060-palace-square-flood', 'Санкт-Петербург 2040-2060 — «Дворцовая площадь под водой»', 'Игроки получают штормовое предупреждение, выносят экспонаты Эрмитажа, управляют дамбами и проводят спасательную операцию на лодках.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2040-2060-palace-square-flood", "title": "Санкт-Петербург 2040-2060 — «Дворцовая площадь под водой»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "disaster-response", "heritage"], "topics": ["cultural-protection", "cooperative-event"], "related_systems": ["narrative-service", "logistics-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2040-2060/quest-028-palace-square-flood.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужен кризисный сценарий по спасению Эрмитажа и Дворцовой площади во время наводнения.", "goal": "Эвакуировать шедевры и защитить площадь от разрушения в условиях ограниченного времени.", "essence": "Игроки получают штормовое предупреждение, выносят экспонаты Эрмитажа, управляют дамбами и проводят спасательную операцию на лодках.", "key_points": ["Дворцовая площадь и Александрийская колонна становятся островом среди воды.", "Требуется координация команд, логистика и защита культурного наследия.", "Исход влияет на репутацию фракций культуры и состояние города."]}, "content": {"sections": [{"id": "warning", "title": "Штормовое предупреждение", "body": "Метеорологи сообщают о критическом подъёме воды. Игроки распределяют роли, подготавливают эвакуационные маршруты и транспорт.\\n", "mechanics_links": ["mechanics/liveops/disaster-alert.yaml"], "assets": []}, {"id": "evacuation", "title": "Эвакуация экспонатов", "body": "В течение двух часов нужно вынести ценные картины и артефакты из Эрмитажа. Каждая партия требует координации с дронами и баржами.\\n", "mechanics_links": ["mechanics/logistics/priority-evacuation.yaml"], "assets": []}, {"id": "flood", "title": "Площадь под водой", "body": "Вода покрывает Дворцовую площадь, Александрийская колонна выступает маяком. Игроки используют лодки и дроны для спасения NPC и\\nконтроля ситуации.\\n", "mechanics_links": ["mechanics/action/rescue-operations.yaml"], "assets": []}, {"id": "outcome", "title": "Итог операции", "body": "При успехе город благодарит героев, культурные ценности спасены. Провал запускает цепочку восстановления и снижает культурную\\nрепутацию.\\n", "mechanics_links": ["mechanics/narrative/outcome-tracking.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 5 000 XP, репутация «Культурное наследие» +20.\\n- Дополнительные бонусы за сохранённые коллекции.\\n", "mechanics_links": ["mechanics/progression/branching-reward.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 821, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Дворцовая площадь под водой» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

