INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-ai-research', 'Берлин 2061-2077 — «Исследование ИИ»', 'Игрок возглавляет лабораторию, строит этичный AGI и принимает решение о его будущем.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-ai-research", "title": "Берлин 2061-2077 — «Исследование ИИ»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["berlin", "ai", "research"], "topics": ["ethical-ai", "scientific-breakthroughs"], "related_systems": ["narrative-service", "research-system", "choice-system"], "related_documents": [{"id": "canon-lore-europe-berlin-2061-2077", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2061-2077/quest-034-ai-research.md", "visibility": "internal", "audience": ["narrative", "quest-design", "technology"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Markdown не выделял конфликт эффективности и этики при создании европейского AGI.", "goal": "Задокументировать шаги научного квеста, отражающие ценности ЕС и их влияние на глобальную ИИ-линию.", "essence": "Игрок возглавляет лабораторию, строит этичный AGI и принимает решение о его будущем.", "key_points": ["DFKI и европейские регуляции формируют уникальный подход к ИИ.", "Дилемма «эффективность против прав человека» определяет исход.", "Квест связывает региональные исследования с глобальной угрозой Blackwall."]}, "content": {"sections": [{"id": "recruitment", "title": "Найм в исследовательский центр", "body": "Игрок проходит строгий отбор и знакомится с принципами GDPR и этичного ИИ.\\nБазовые решения задают параметры лаборатории и доступ к данным.\\n", "mechanics_links": [], "assets": []}, {"id": "development", "title": "Разработка европейского AGI", "body": "Команда формирует архитектуру, вводит этические протоколы и оценочные тесты.\\nИгрок распределяет ресурсы между скоростью и прозрачностью.\\n", "mechanics_links": [], "assets": []}, {"id": "conflict", "title": "Конфликт эффективности и этики", "body": "Давление корпораций требует ускорить релиз в ущерб принципам.\\nВыбор влияет на репутацию, безопасность данных и доступ к финансированию.\\n", "mechanics_links": [], "assets": []}, {"id": "resolution", "title": "Судьба проекта", "body": "Игрок решает опубликовать AGI, передать корпорации или уничтожить результаты.\\nПоследствия меняют отношение ЕС, корпораций и подпольных ИИ-групп.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 20 000 XP, до 1 000 000 кредов (при коммерциализации), уникальные улучшения для research-system.\\n- Глобальные изменения в сюжетах Blackwall и ИИ-фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "DFKI", "definition": "Немецкий исследовательский центр ИИ, задающий стандарты этичного искусственного интеллекта."}], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертация квеста «Исследование ИИ» в knowledge-entry с фокусом на этические дилеммы AGI."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

