INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-brussels-2020-2029-grand-place', 'Брюссель 2020-2029 — Гранд Плас', 'Игрок постигает красоту золотых фасадов гильдейских домов и временные чудеса цветочных ковров.', 'side', 5, 15, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enter_grand_place", "text": "Войти на Grand Place и оценить архитектурный ансамбль золотых фасадов гильдейских домов", "type": "explore", "target": "grand_place_square", "count": 1, "optional": false}, {"id": "visit_hotel_de_ville", "text": "Посетить Hôtel de Ville (Ратушу) и изучить ее готическую архитектуру", "type": "investigate", "target": "brussels_city_hall", "count": 1, "optional": false}, {"id": "explore_guild_houses", "text": "Исследовать гильдейские дома и узнать историю ремесленных корпораций XVII века", "type": "explore", "target": "guild_houses_facades", "count": 1, "optional": false}, {"id": "witness_night_illumination", "text": "Вернуться вечером и стать свидетелем подсветки фасадов, создающей волшебную атмосферу", "type": "interact", "target": "evening_illumination_show", "count": 1, "optional": false}, {"id": "experience_flower_carpet", "text": "Если сезон подходящий, увидеть цветочный ковёр из 750 тысяч цветов на площади", "type": "investigate", "target": "flower_carpet_event", "count": 1, "optional": true}]'::jsonb, '{"experience": 1200, "money": 0, "items": [{"item_id": "achievement_grand_place_master", "quantity": 1}], "reputation": {"Brussels_Architecture": 20, "Cultural_Heritage": 15}, "unlocks": ["architecture_quests", "cultural_events_access"]}'::jsonb, '[{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"culture": 20, "history": 15}, "reputation": {"Brussels_Architecture": 25, "European_Heritage": 20}, "unlocks": ["exclusive_architecture_sites", "heritage_events_network"]}}]'::jsonb, '{"metadata": {"id": "quest-brussels-2020-2029-grand-place", "title": "Брюссель 2020-2029 — Гранд Плас", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-13T19:05:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "content_writer", "contact": "content@necp.game"}], "tags": ["europe", "brussels", "architecture", "history", "unesco"], "topics": ["architectural-tour", "cultural-heritage", "city-center"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "culture-service", "exploration-service"], "related_documents": [{"id": "canon-region-europe-brussels-2020-2093", "relation": "references"}, {"id": "quest-system-architecture", "relation": "implements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/brussels/2020-2029/quest-008-grand-place.md", "visibility": "internal", "audience": ["content", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "content_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о Grand Place не демонстрировал UNESCO-статус и архитектурное великолепие площади.", "goal": "Создать образовательный маршрут, раскрывающий историю и культурное значение главной площади Брюсселя.", "essence": "Игрок постигает красоту золотых фасадов гильдейских домов и временные чудеса цветочных ковров.", "key_points": ["Grand Place - сердце Брюсселя, признанное UNESCO как шедевр архитектуры", "Квест сочетает архитектурный туризм, историю и сезонные события", "Подчеркивается контраст между дневной и вечерней подсветкой"]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": 15, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enter_grand_place", "text": "Войти на Grand Place и оценить архитектурный ансамбль золотых фасадов гильдейских домов", "type": "explore", "target": "grand_place_square", "count": 1, "optional": false}, {"id": "visit_hotel_de_ville", "text": "Посетить Hôtel de Ville (Ратушу) и изучить ее готическую архитектуру", "type": "investigate", "target": "brussels_city_hall", "count": 1, "optional": false}, {"id": "explore_guild_houses", "text": "Исследовать гильдейские дома и узнать историю ремесленных корпораций XVII века", "type": "explore", "target": "guild_houses_facades", "count": 1, "optional": false}, {"id": "witness_night_illumination", "text": "Вернуться вечером и стать свидетелем подсветки фасадов, создающей волшебную атмосферу", "type": "interact", "target": "evening_illumination_show", "count": 1, "optional": false}, {"id": "experience_flower_carpet", "text": "Если сезон подходящий, увидеть цветочный ковёр из 750 тысяч цветов на площади", "type": "investigate", "target": "flower_carpet_event", "count": 1, "optional": true}], "rewards": {"experience": 1200, "money": 0, "items": [{"item_id": "achievement_grand_place_master", "quantity": 1}], "reputation": {"Brussels_Architecture": 20, "Cultural_Heritage": 15}, "unlocks": ["architecture_quests", "cultural_events_access"]}, "branches": [{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"culture": 20, "history": 15}, "reputation": {"Brussels_Architecture": 25, "European_Heritage": 20}, "unlocks": ["exclusive_architecture_sites", "heritage_events_network"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Grand Place - жемчужина Брюсселя и шедевр барочной архитектуры, включенный в список UNESCO.\\nПлощадь окружена золотыми фасадами гильдейских домов XVII века, а ее вечерняя подсветка\\nсоздает неповторимую атмосферу волшебства.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы квеста", "body": "1. **Вход на площадь**: Осмотр архитектурного ансамбля и золотых фасадов.\\n2. **Ратуша**: Изучение готической архитектуры Hôtel de Ville.\\n3. **Гильдейские дома**: Исследование истории ремесленных корпораций.\\n4. **Вечерняя подсветка**: Свидетельство волшебного освещения фасадов.\\n5. **Цветочный ковёр**: Возможное участие в сезонном событии с цветами.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Историческое значение Брюсселя", "body": "Grand Place символизирует богатство и мощь средневекового Брюсселя. Ее архитектура отражает\\nпереход от готического стиля к барокко и служит напоминанием о ремесленных традициях города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт**: 1 200 XP\\n- **Валюта**: 0 (бесплатный квест)\\n- **Предметы**: Ачивка «Хозяин Grand Place»\\n- **Репутация**: +20 к архитектурной репутации, +15 к культурному наследию\\n- **Разблокировки**: Доступ к архитектурным квестам и культурным событиям\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые механики", "body": "Квест сочетает исследование, историческое образование и эстетическое наслаждение.\\nИгрок учится понимать взаимосвязь архитектуры и городской идентичности.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "Связан с архитектурной линией Брюсселя и историческими квестами. Открывает путь к более\\nглубокому пониманию европейского культурного наследия и барочной архитектуры.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 56, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer", "changes": "Структурирован квест согласно архитектуре системы квестов. Добавлена quest_definition 