INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2061-2077-romanov-treasure', 'Санкт-Петербург 2061-2077 — «Сокровища Романовых»', 'Игрок собирает подсказки, спускается в охраняемые тоннели и сталкивается с моральным выбором — вернуть, продать или присвоить ценности.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2061-2077-romanov-treasure", "title": "Санкт-Петербург 2061-2077 — «Сокровища Романовых»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "treasure-hunt", "romanov-legacy"], "topics": ["high-stakes-adventure", "branching-outcome"], "related_systems": ["narrative-service", "dungeon-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-032-romanov-treasure.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "extreme"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало эпического данжен-квеста про императорские реликвии Романовых с выбором судьбы сокровищ.", "goal": "Позволить игроку расшифровать дневник камер-гера, пройти подземелья Зимнего дворца и решить, кому достанутся регалии.", "essence": "Игрок собирает подсказки, спускается в охраняемые тоннели и сталкивается с моральным выбором — вернуть, продать или присвоить ценности.", "key_points": [{"Включены легендарные регалии": "корона, скипетр, яйца Фаберже."}, "Данжен сочетает ловушки, роботов и исследование архивов.", "Выбор окончания запускает глобальные последствия."]}, "content": {"sections": [{"id": "prologue", "title": "Дневник камергера", "body": "Игрок находит архивный дневник, расшифровывает шифры и отмечает координаты тайника.\\n", "mechanics_links": ["mechanics/investigation/codebreaking.yaml"], "assets": []}, {"id": "descent", "title": "Подземные залы", "body": "Нужно пройти под Зимним дворцом, отключить ловушки и обойти охранных роботов.\\n", "mechanics_links": ["mechanics/dungeon/security-bypass.yaml"], "assets": []}, {"id": "vault", "title": "Тайник сокровищ", "body": "Игрок открывает несколько сейфов, оценивает реликвии и решает, какие предметы брать.\\n", "mechanics_links": ["mechanics/loot/treasure-evaluation.yaml"], "assets": []}, {"id": "decision", "title": "Судьба сокровищ", "body": "Выбор между возвращением государству, продажей коллекционерам или личным присвоением с соответствующими последствиями.\\n", "mechanics_links": ["mechanics/narrative/branching-endings.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 20 000 XP.\\n- Либо музейный титул, либо 2 000 000 000 еддис, либо уникальная коллекция.\\n- Титул «Искатель Империи».\\n", "mechanics_links": ["mechanics/progression/legacy-title.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 825, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Сокровища Романовых» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, 