INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2061-2077-russian-museum-night', 'Санкт-Петербург 2061-2077 — «Ночь в Русском музее»', 'Игрок остаётся в музее после закрытия, беседует с цифровыми Репиным, Айвазовским, Врубелем и Кандинским и входит внутрь их картин.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2061-2077-russian-museum-night", "title": "Санкт-Петербург 2061-2077 — «Ночь в Русском музее»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "museum", "ai-art"], "topics": ["immersive-art", "educational-experience"], "related_systems": ["narrative-service", "education-service", "liveops-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-033-russian-museum-night.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Музейной линии требовалось ночное событие с оживающими шедеврами русского искусства.", "goal": "Погрузить игрока в VR-опыт Русского музея и познакомить с ИИ-художниками.", "essence": "Игрок остаётся в музее после закрытия, беседует с цифровыми Репиным, Айвазовским, Врубелем и Кандинским и входит внутрь их картин.", "key_points": ["Залы Русского музея превращаются в живые полотна.", "ИИ-художники делятся историями создания шедевров.", "Квест усиливает культурный статус игрока."]}, "content": {"sections": [{"id": "invitation", "title": "Приглашение на ночную сессию", "body": "Игрок получает особый пропуск, позволяющий остаться в музее после закрытия. Куратор-ИИ объясняет правила поведения среди оживающих\\nэкспонатов.\\n", "mechanics_links": ["mechanics/exploration/guided-tour.yaml"], "assets": []}, {"id": "awakening", "title": "Пробуждение галерей", "body": "Свет гаснет, картины активируют VR-слои. ИИ-версии Репина, Айвазовского, Врубеля и Кандинского по очереди приглашают игрока войти\\nв их миры.\\n", "mechanics_links": ["mechanics/narrative/cinematic-trigger.yaml"], "assets": []}, {"id": "immersion", "title": "Погружение в полотна", "body": "Игрок попадает внутрь картин, выполняет задания: помогает бурлакам, успокаивает шторм Айвазовского, проходит демонические сны Врубеля,\\nстроит абстракцию с Кандинским.\\n", "mechanics_links": ["mechanics/vr/scene-transition.yaml"], "assets": []}, {"id": "finale", "title": "Рассвет и выводы", "body": "На рассвете ИИ-художники обсуждают значение искусства XXI века, фиксируют прогресс игрока и дарят цифровой каталог коллекции.\\n", "mechanics_links": ["mechanics/narrative/outcome-tracking.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 3 000 XP, атрибут «Культура» +20.\\n- Ачивка «Друг художников» и доступ к музейным лекциям LiveOps.\\n", "mechanics_links": ["mechanics/progression/achievement-grant.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 826, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Ночь в Русском музее» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

