INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-tower-heist', 'Лондон 2020-2029 — Ограбление Башни', 'Команда игроков планирует налёт на корп-хранилище в Тауэре, чтобы похитить легендарный имплант и скрыться от элитной охраны.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-tower-heist", "title": "Лондон 2020-2029 — Ограбление Башни", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "heist", "royal"], "topics": ["main_story", "combat"], "related_systems": ["narrative-service", "stealth-system", "crew-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2020-2029/quest-002-tower-heist.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сюжет ограбления Лондонского Тауэра находился в Markdown и не был включён в канон.", "goal": "Задокументировать основной квест о краже королевского нейроимпланта.", "essence": "Команда игроков планирует налёт на корп-хранилище в Тауэре, чтобы похитить легендарный имплант и скрыться от элитной охраны.\\n", "key_points": ["Расписаны этапы от планирования и набора команды до проникновения и побега.", "Подчёркнута уникальная награда — «Королевский Нейроимплант» и старт линии «Королевское Наследие».", {"Определены требования к ролям": "взломщик, стрелок, водитель."}]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Тауэр превращён в корпоративное хранилище королевских реликвий. Игрок организует сложное ограбление с множеством вариантов прохождения.\\n", "mechanics_links": [], "assets": []}, {"id": "planning_phase", "title": "Подготовка", "body": "1. Собрать разведданные о системах безопасности.\\n2. Нанять специалистов по взлому, боевому сопровождению и управлению транспортом.\\n3. Выбрать стратегию проникновения: стелс, силовой штурм или хакерская атака.\\n", "mechanics_links": [], "assets": []}, {"id": "execution", "title": "Проведение ограбления", "body": "1. Получить доступ в хранилище, обходя датчики и элитную охрану.\\n2. Извлечь «Королевский Нейроимплант» из защищённого контейнера.\\n3. Организовать побег по заранее подготовленному маршруту.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "2 500 XP, 50 000 едди с делёжкой и легендарный имплант. Успех открывает линию «Королевское Наследие».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-tower-heist"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Ограбление Башни» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

