INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-saint-petersburg-2061-2077-kunstkamera-oddities', 'Санкт-Петербург 2061-2077 — «Кунсткамера странностей»', 'Ночью оживают экспонаты, появляется ИИ-Пётр I и предлагает выбрать — сохранить, модернизировать или закрыть коллекцию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-saint-petersburg-2061-2077-kunstkamera-oddities", "title": "Санкт-Петербург 2061-2077 — «Кунсткамера странностей»", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["saint-petersburg", "museum", "horror"], "topics": ["uncanny-exploration", "ethical-choice"], "related_systems": ["narrative-service", "education-service", "wellbeing-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/saint-petersburg/2061-2077/quest-035-kunstkamera-oddities.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Линии музеев не хватало мрачного опыта с наследием Петра I.", "goal": "Показать коллекцию Кунсткамеры и дать игроку решить судьбу «кабинета редкостей».", "essence": "Ночью оживают экспонаты, появляется ИИ-Пётр I и предлагает выбрать — сохранить, модернизировать или закрыть коллекцию.", "key_points": ["Квест сочетает хоррор-элементы с историческим контекстом.", "Решение влияет на будущие выставки и общественное мнение.", "Игрок общается с цифровым Петром I, создателем музея."]}, "content": {"sections": [{"id": "entrance", "title": "Ночной визит", "body": "Игрок прибывает на Стрелку Васильевского острова. Экспонаты в банках с раствором слегка шевелятся, а гид предупреждает о странных\\nявлениях.\\n", "mechanics_links": ["mechanics/exploration/guided-tour.yaml"], "assets": []}, {"id": "awakening", "title": "Ожившие редкости", "body": "В полночь банки трескаются, проявляются голограммы «уродств». Игрок успокаивает посетителей и изучает каждую аномалию.\\n", "mechanics_links": ["mechanics/horror/tension-event.yaml"], "assets": []}, {"id": "dialogue", "title": "Встреча с ИИ-Петром I", "body": "Пётр I анализирует реакцию игрока и рассказывает, зачем создал Кунсткамеру. Предлагает три стратегии развития коллекции.\\n", "mechanics_links": ["mechanics/narrative/dialog-choice.yaml"], "assets": []}, {"id": "decision", "title": "Судьба коллекции", "body": "Игрок выбирает: сохранить музей как есть, модернизировать и превратить в научный центр или закрыть доступ, передав экспонаты в архив.\\n", "mechanics_links": ["mechanics/narrative/branching-outcome.yaml"], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2 000 XP, атрибут «Культура» +10.\\n- Ачивка «Любитель странностей» и эмоут «Экспонат».\\n", "mechanics_links": ["mechanics/progression/achievement-grant.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 828, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Кунсткамера странностей» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objective