INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2078-2093-ich-bin-ein-berliner', 'Берлин 2078-2093 — «Я — Берлинец»', 'Игрок проходит через реконструкцию Rathaus Schöneberg, произносит «Ich bin ein Berliner» и получает статус почётного жителя.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2078-2093-ich-bin-ein-berliner", "title": "Берлин 2078-2093 — «Я — Берлинец»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "identity"], "topics": ["city_quests", "historical_memory"], "related_systems": ["narrative-service", "immersion-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-048-ich-bin-ein-berliner.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Игрокам нужен философский эпилог, объясняющий, что значит быть частью берлинской культуры в киберпанковскую эпоху.", "goal": "Перенести культовую речь Кеннеди в VR и переосмыслить её для современного Берлина.", "essence": "Игрок проходит через реконструкцию Rathaus Schöneberg, произносит «Ich bin ein Berliner» и получает статус почётного жителя.", "key_points": ["Речь 1963 года переинтерпретируется в контексте свободы и творчества города.", "VR-анимация даёт возможность пережить исторический момент от первого лица.", "Квест закрепляет идеологическую линию перед финалом сезона."]}, "content": {"sections": [{"id": "introduction", "title": "Контекст", "body": "После культурных и символических задач игрок получает приглашение посетить Rathaus Schöneberg,\\nгде была произнесена известная речь. Кураторы предлагают VR-сессию, чтобы ощутить дух единства и свободы.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Rathaus Schöneberg и активировать VR-реконструкцию 1963 года.\\n2. Наблюдать речь Кеннеди и реакции толпы, анализируя исторический контекст.\\n3. Произнести фразу «Ich bin ein Berliner» в интерактивной сцене и выбрать своё понимание свободы.\\n4. Получить ответы горожан о том, что значит быть берлинцем в 2093 году.\\n5. Зафиксировать личное кредо игрока и передать его в городской архив.\\n", "mechanics_links": [], "assets": []}, {"id": "mood", "title": "Атмосфера", "body": "Звуковое оформление сочетает записи с площади 1963 года и современный техно-саунд.\\nХроники показывают эволюцию города от разделённого к свободному мегаполису.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Награды включают 2,000 XP, титул «Почётный Берлинец» и эмоциональный бафф «Inspiration» (повышение харизмы).\\nФинансовое вознаграждение отсутствует.\\n", "mechanics_links": [], "assets": []}, {"id": "relations", "title": "Связи", "body": "Квест идёт после «Символа Медведя» и «Техно Единства» и перед финальными событиями «Вечного Техно».\\nИспользуется в хрониках города и в обучении новых игроков.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Я — Берлинец» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

