INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-brussels-2020-2029-eu-capital', 'Брюссель 2020-2029 — Столица ЕС', 'Игрок исследует европейские институты, знакомясь с парламентом, комиссией и символикой объединённой Европы.', 'side', 10, 25, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "visit_european_quarter", "text": "Посетить Европейский квартал и ознакомиться с основными зданиями институтов ЕС", "type": "explore", "target": "european_quarter_area", "count": 1, "optional": false}, {"id": "explore_parlamentarium", "text": "Зайти в Parlamentarium для интерактивного знакомства с историей и механизмами ЕС", "type": "interact", "target": "parlamentarium_museum", "count": 1, "optional": false}, {"id": "observe_european_parliament", "text": "Посетить Европейский парламент и наблюдать за работой депутатов", "type": "investigate", "target": "european_parliament_building", "count": 1, "optional": false}, {"id": "study_berlaymont", "text": "Исследовать комплекс Берлемон и окружение Европейской комиссии", "type": "explore", "target": "berlaymont_commission_complex", "count": 1, "optional": false}, {"id": "document_eu_symbols", "text": "Зафиксировать ансамбль флагов всех 27 стран ЕС и символику объединённой Европы", "type": "interact", "target": "eu_flags_square", "count": 1, "optional": false}]'::jsonb, '{"experience": 2500, "money": 0, "items": [{"item_id": "achievement_eurocrat", "quantity": 1}], "reputation": {"EU_Influence": 25, "Brussels_Politics": 15}, "unlocks": ["eu_political_quests", "diplomacy_training"]}'::jsonb, '[{"branch_id": "bureaucracy_enthusiast", "description": "Стать энтузиастом европейской бюрократии", "consequences": {"skills": {"politics": 25, "diplomacy": 20}, "reputation": {"EU_Influence": 30}, "unlocks": ["exclusive_eu_access", "commissioner_network"]}}]'::jsonb, '{"metadata": {"id": "quest-brussels-2020-2029-eu-capital", "title": "Брюссель 2020-2029 — Столица ЕС", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-13T18:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "content_writer", "contact": "content@necp.game"}], "tags": ["europe", "brussels", "politics", "eu", "bureaucracy"], "topics": ["political-tour", "european-union", "diplomacy"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "diplomacy-service", "education-service", "governance-service"], "related_documents": [{"id": "canon-region-europe-brussels-2020-2093", "relation": "references"}, {"id": "quest-system-architecture", "relation": "implements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/brussels/2020-2029/quest-001-eu-capital.md", "visibility": "internal", "audience": ["content", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "content_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест «Столица ЕС» не демонстрировал политическую мощь Брюсселя как центра европейской бюрократии.", "goal": "Показать игроку инфраструктуру Европейского квартала и роль Брюсселя в принятии решений ЕС.", "essence": "Игрок исследует европейские институты, знакомясь с парламентом, комиссией и символикой объединённой Европы.", "key_points": ["Европейский квартал - сердце бюрократии ЕС с 40+ тысячами еврократов", "Подчёркивается масштабы влияния и символика 27 флагов стран-членов", "Квест раскрывает политические механизмы и дипломатическую роль Брюсселя"]}, "quest_definition": {"quest_type": "side", "level_min": 10, "level_max": 25, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "visit_european_quarter", "text": "Посетить Европейский квартал и ознакомиться с основными зданиями институтов ЕС", "type": "explore", "target": "european_quarter_area", "count": 1, "optional": false}, {"id": "explore_parlamentarium", "text": "Зайти в Parlamentarium для интерактивного знакомства с историей и механизмами ЕС", "type": "interact", "target": "parlamentarium_museum", "count": 1, "optional": false}, {"id": "observe_european_parliament", "text": "Посетить Европейский парламент и наблюдать за работой депутатов", "type": "investigate", "target": "european_parliament_building", "count": 1, "optional": false}, {"id": "study_berlaymont", "text": "Исследовать комплекс Берлемон и окружение Европейской комиссии", "type": "explore", "target": "berlaymont_commission_complex", "count": 1, "optional": false}, {"id": "document_eu_symbols", "text": "Зафиксировать ансамбль флагов всех 27 стран ЕС и символику объединённой Европы", "type": "interact", "target": "eu_flags_square", "count": 1, "optional": false}], "rewards": {"experience": 2500, "money": 0, "items": [{"item_id": "achievement_eurocrat", "quantity": 1}], "reputation": {"EU_Influence": 25, "Brussels_Politics": 15}, "unlocks": ["eu_political_quests", "diplomacy_training"]}, "branches": [{"branch_id": "bureaucracy_enthusiast", "description": "Стать энтузиастом европейской бюрократии", "consequences": {"skills": {"politics": 25, "diplomacy": 20}, "reputation": {"EU_Influence": 30}, "unlocks": ["exclusive_eu_access", "commissioner_network"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Брюссель - де-факто столица Европейского союза, где сосредоточены ключевые институты и работает свыше 40 тысяч\\nеврократов. Квест знакомит игрока с инфраструктурой Европейского квартала и его ролью в объединении Европы.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы квеста", "body": "1. **Европейский квартал**: Исследовать основные здания институтов ЕС и их архитектуру.\\n2. **Parlamentarium**: Интерактивное знакомство с историей ЕС и процессом принятия решений.\\n3. **Европейский парламент**: Наблюдение за работой депутатов и политическими дебатами.\\n4. **Берлемон**: Изучение комплекса Европейской комиссии и исполнительной власти ЕС.\\n5. **Символика ЕС**: Фиксация флагов 27 стран-членов и понимание единства Европы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные особенности Брюсселя", "body": "Европейский квартал занимает значительную часть восточного Брюсселя, создавая контраст между историческим\\nцентром города и современными стеклянными зданиями институтов. Здесь сосредоточена политическая мощь ЕС,\\nвлияющая на жизнь миллионов европейцев.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт**: 2 500 XP\\n- **Валюта**: 0 (образовательный квест)\\n- **Репутация**: +25 к влиянию в ЕС, +15 к политической репутации Брюсселя\\n- **Ачивка**: «Еврократ» - символ понимания европейской бюрократии\\n- **Разблокировки**: Доступ к политическим квестам ЕС и дипломатическому обучению\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые механики", "body": "Квест сочетает образовательные элементы с политическими механиками. Игрок изучает процесс принятия решений ЕС,\\nзнакомится с дипломатией и понимает влияние европейской бюрократии на глобальные события.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "Связан с политической линией Брюсселя, дипломатическими миссиями и сюжетами о европейской интеграции.\\nВлияет на доступ к международным квестам и дипломатическим отношениям.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 56, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer", "changes": "Структурирован квест согласно архитектуре системы квестов. Добавлена quest_definition с objectives, rewards и branches. Детализирован контент с фокусом на политические механики Брюсселя."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

