-- Quest: canon-quest-berlin-2078-2093-bear-symbol
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2078-2093-bear-symbol', 'Берлин 2078-2093 — «Символ Медведя»', 'Игрок собирает фотографии Buddy Bears по всему Берлину и завершает встречей с последним медведем у Рейхстага.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2078-2093-bear-symbol", "title": "Берлин 2078-2093 — «Символ Медведя»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "symbol"], "topics": ["city_quests", "cultural_identity"], "related_systems": ["narrative-service", "world-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-046-bear-symbol.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужна эмоциональная сцена, закрепляющая культурный символ Берлина перед финалом городского цикла.", "goal": "Дать игроку возможность попрощаться с образом «Берлинского медведя» и собрать визуальные артефакты города.", "essence": "Игрок собирает фотографии Buddy Bears по всему Берлину и завершает встречей с последним медведем у Рейхстага.", "key_points": ["Акцент на городском символе, присутствующем с XIII века.", "Коллекционная механика через Buddy Bears усиливает туристическую линию.", "Финальная сцена у Рейхстага закрывает культурную арку."]}, "content": {"sections": [{"id": "context", "title": "Контекст", "body": "Квест открывается после ключевых событий эпохи 2078-2093. Город готовится к перезапуску лиги,\\nи кураторы просят игрока помочь задокументировать всех «Берлинских медведей», чтобы сохранить дух столицы.\\n", "mechanics_links": [], "assets": []}, {"id": "objectives", "title": "Этапы", "body": "1. Получить карту расположения Buddy Bears на улицах города.\\n2. Посетить минимум 50 статуй и сделать AR-фотографии с уникальными дизайнами.\\n3. Загрузить коллекцию в городской архив и получить приглашение к Рейхстагу.\\n4. Встретить последнего медведя и оставить прощальное сообщение городу.\\n", "mechanics_links": [], "assets": []}, {"id": "flavor", "title": "Детали атмосферы", "body": "Конструкции Buddy Bears раскрашены художниками из разных стран, отражая мультикультурность Берлина.\\nВо время финальной сцены слышны хроники Berlinale и другие отсылки к «медвежьей» символике города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "Игрок получает 1,000 XP, ачивку «Друг Медведя» и коллекцию медвежьих стикеров для персонализации интерфейса.\\nВалюта не начисляется, что подчёркивает культурную, а не экономическую ценность квеста.\\n", "mechanics_links": [], "assets": []}, {"id": "integration", "title": "Связи", "body": "Задание поддерживает культурную линию Берлина и рекомендуется перед квестами «Я — Берлинец» и «Вечное Техно».\\nСобранные материалы используются в хрониках города и маркетинговых кампаниях сезона.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Символ Медведя» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

