INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-brussels-2020-2029-atomium', 'Брюссель 2020-2029 — Атомиум', 'Игрок исследует увеличенный атом железа, постигая оптимизм послевоенной эпохи и научный прогресс.', 'side', 5, 15, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "explore_atomium_structure", "text": "Исследовать внешнюю структуру Atomium - 9 сфер, соединенных трубами, символизирующих атом железа", "type": "explore", "target": "atomium_exterior_structure", "count": 1, "optional": false}, {"id": "ascend_escalators", "text": "Подняться по эскалаторам внутри труб и посетить выставки в сферах", "type": "interact", "target": "atomium_interior_escalators", "count": 1, "optional": false}, {"id": "reach_observation_deck", "text": "Добраться до верхней смотровой площадки и оценить панораму Брюсселя", "type": "explore", "target": "atomium_observation_deck", "count": 1, "optional": false}, {"id": "study_expo_context", "text": "Изучить исторический контекст Expo 1958 и атомный оптимизм 1950-х", "type": "investigate", "target": "expo_1958_historical_context", "count": 1, "optional": false}, {"id": "reflect_atomic_symbolism", "text": "Осмыслить символизм Atomium как моста между наукой, архитектурой и искусством", "type": "investigate", "target": "atomic_age_symbolism", "count": 1, "optional": false}]'::jsonb, '{"experience": 1200, "money": -15, "items": [{"item_id": "achievement_atomic_explorer", "quantity": 1}], "reputation": {"Brussels_Architecture": 15, "Historical_Knowledge": 10}, "unlocks": ["architecture_quests", "expo_history_access"]}'::jsonb, '[{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"culture": 15, "exploration": 10}, "reputation": {"Brussels_Architecture": 20}, "unlocks": ["exclusive_architecture_sites", "modernist_network"]}}]'::jsonb, '{"metadata": {"id": "quest-brussels-2020-2029-atomium", "title": "Брюссель 2020-2029 — Атомиум", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "1.0.0", "last_updated": "2025-12-13T18:50:00+00:00", "concept_approved": true, "concept_reviewed_at": "", "owners": [{"role": "content_writer", "contact": "content@necp.game"}], "tags": ["europe", "brussels", "architecture", "history", "futurism"], "topics": ["architectural-tour", "expo-heritage", "atomic-age"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "culture-service", "exploration-service"], "related_documents": [{"id": "canon-region-europe-brussels-2020-2093", "relation": "references"}, {"id": "quest-system-architecture", "relation": "implements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/brussels/2020-2029/quest-005-atomium.md", "visibility": "internal", "audience": ["content", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "content_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест об Atomium не демонстрировал архитектурное значение и исторический контекст Expo 1958.", "goal": "Создать образовательный маршрут, раскрывающий символизм атомного века и футуристическую архитектуру.", "essence": "Игрок исследует увеличенный атом железа, постигая оптимизм послевоенной эпохи и научный прогресс.", "key_points": ["Atomium - символ атомного века, построенный к Expo 1958 как увеличенный атом железа", "Квест сочетает архитектурный туризм, историческое образование и символизм науки", "Подчеркивается контраст между оптимизмом 1950-х и современными реалиями"]}, "quest_definition": {"quest_type": "side", "level_min": 5, "level_max": 15, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_atomium_structure", "text": "Исследовать внешнюю структуру Atomium - 9 сфер, соединенных трубами, символизирующих атом железа", "type": "explore", "target": "atomium_exterior_structure", "count": 1, "optional": false}, {"id": "ascend_escalators", "text": "Подняться по эскалаторам внутри труб и посетить выставки в сферах", "type": "interact", "target": "atomium_interior_escalators", "count": 1, "optional": false}, {"id": "reach_observation_deck", "text": "Добраться до верхней смотровой площадки и оценить панораму Брюсселя", "type": "explore", "target": "atomium_observation_deck", "count": 1, "optional": false}, {"id": "study_expo_context", "text": "Изучить исторический контекст Expo 1958 и атомный оптимизм 1950-х", "type": "investigate", "target": "expo_1958_historical_context", "count": 1, "optional": false}, {"id": "reflect_atomic_symbolism", "text": "Осмыслить символизм Atomium как моста между наукой, архитектурой и искусством", "type": "investigate", "target": "atomic_age_symbolism", "count": 1, "optional": false}], "rewards": {"experience": 1200, "money": -15, "items": [{"item_id": "achievement_atomic_explorer", "quantity": 1}], "reputation": {"Brussels_Architecture": 15, "Historical_Knowledge": 10}, "unlocks": ["architecture_quests", "expo_history_access"]}, "branches": [{"branch_id": "architecture_enthusiast", "description": "Стать энтузиастом архитектуры", "consequences": {"skills": {"culture": 15, "exploration": 10}, "reputation": {"Brussels_Architecture": 20}, "unlocks": ["exclusive_architecture_sites", "modernist_network"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Atomium - икона архитектуры XX века, символизирующая веру в атомную энергию и научный прогресс.\\nПостроенный к Expo 1958, этот 102-метровый монумент представляет увеличенный атом железа\\nи отражает оптимизм послевоенной эпохи.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы квеста", "body": "1. **Внешняя структура**: Изучение 9 сфер и труб, символизирующих кристаллическую решетку железа.\\n2. **Внутреннее восхождение**: Подъем по эскалаторам и посещение выставочных пространств.\\n3. **Смотровая площадка**: Панорамный вид на Брюссель с высоты атома.\\n4. **Исторический контекст**: Изучение Expo 1958 и атомного оптимизма.\\n5. **Символизм**: Осмысление значения Atomium как моста науки и искусства.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Архитектурное значение Брюсселя", "body": "Atomium - не просто памятник, а символ европейского модернизма. Его футуристический дизайн\\nоказал влияние на архитектуру Брюсселя и стал частью коллективной памяти города.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт**: 1 200 XP\\n- **Валюта**: -15 (входной билет)\\n- **Предметы**: Ачивка «Атомный исследователь»\\n- **Репутация**: +15 к архитектурной репутации Брюсселя, +10 к историческим знаниям\\n- **Разблокировки**: Доступ к архитектурным квестам и историческим событиям\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые механики", "body": "Квест сочетает образовательные элементы, исследование и символизм. Игрок учится понимать\\nвзаимосвязь науки, архитектуры и исторического контекста.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "Связан с архитектурной линией Брюсселя и историческими квестами. Открывает путь к более\\nглубокому пониманию модернизма и европейской послевоенной истории.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 56, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer", "changes": "Структурирован квест согласно архитектуре системы квестов. Добавлена quest_definition с objectives, rewards и branches. Сфокусирован на архитектурном и историческом значении Atomium."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

