INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-berlin-2078-2093-eternal-techno', 'Берлин 2078-2093 — «Вечное Техно»', 'Игрок возвращается в Berghain для последнего танца, выбирает наследие и прощается со столицей техно перед новой лигой.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-berlin-2078-2093-eternal-techno", "title": "Берлин 2078-2093 — «Вечное Техно»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["berlin", "quest", "legacy"], "topics": ["city_quests", "seasonal_league"], "related_systems": ["narrative-service", "world-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/berlin/2078-2093/quest-050-eternal-techno.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Финал берлинской арки требует эмоционального завершения, чтобы игроки ощущали ценность всех 50 квестов города.", "goal": "Создать завершающий квест, закрепляющий наследие Берлина и подготавливающий игроков к перезапуску лиги.", "essence": "Игрок возвращается в Berghain для последнего танца, выбирает наследие и прощается со столицей техно перед новой лигой.", "key_points": ["Финальный выбор наследия синхронизирован с системой Legacy.", "Берлин позиционируется как «город вечно звучащего техно».", "Квест связывает все предыдущие 49 историй города в единый эпилог."]}, "content": {"sections": [{"id": "setup", "title": "Контекст", "body": "Квест активируется после прохождения всей линии Берлина. Игрок получает приглашение на последний сет в Berghain,\\nгде под свет стробов необходимо закрепить итоговые выборы и подготовиться к переходу в новую лигу.\\n", "mechanics_links": [], "assets": []}, {"id": "phases", "title": "Этапы прохождения", "body": "1. Принять Legacy-решение и сформировать пакет наследия.\\n2. Войти в Berghain — место, где начиналась история.\\n3. Станцевать финальный сет, наблюдая за откликом толпы.\\n4. Прослушать прощальную речь DJ и увидеть катсцену про вечное техно.\\n5. Завершить лигу и перенестись к старту нового сезона.\\n", "mechanics_links": [], "assets": []}, {"id": "atmosphere", "title": "Атмосфера и визуальные мотивы", "body": "Тяжёлый бас, вспышки неона, плотные толпы техно-фанатов и голос за кадром подчеркивают, что Берлин «танцует вечно».\\nКатсцена завершается затемнением экрана и приглашением вернуться в новую эпоху.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает титул «Ветеран Берлина 2020-2093», максимальный Legacy-бонус и нематериальный эффект —\\nэмоциональное закрытие городского цикла. XP не начисляется, так как квест символизирует конец лиги.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи и зависимые линии", "body": "Квест открывается только после завершения всех 49 предыдущих заданий Берлина и переводит игроков\\nв стартовый контент новой лиги. Связан с механиками Legacy и глобальными событиями перезапуска.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертация квеста «Вечное Техно» в формат knowledge-entry."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

