INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-sherlock-ai', 'Лондон 2030-2039 — ИИ Шерлок', 'Игрок сотрудничает с цифровой версией Шерлока Холмса, осматривает место преступления, собирает улики и выводит убийцу на чистую воду.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-sherlock-ai", "title": "Лондон 2030-2039 — ИИ Шерлок", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "detective", "ai"], "topics": ["crime", "investigation"], "related_systems": ["narrative-service", "investigation-system", "companion-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2030-2039/quest-013-sherlock-ai.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Детективный квест про ИИ Шерлока был сохранён в Markdown и отсутствовал в канонической базе.", "goal": "Представить расследование, ведущееся вместе с ИИ-партнёром, в формате YAML для дальнейшего дизайна.", "essence": "Игрок сотрудничает с цифровой версией Шерлока Холмса, осматривает место преступления, собирает улики и выводит убийцу на чистую воду.\\n", "key_points": [{"Описан набор этапов расследования": "найм, осмотр сцены, сбор пяти улик и финальная дедукция."}, "Зафиксированы особенности персонажа — классические фразы, викторианская атмосфера и эстетика офиса.", {"Определены награды": "опыт, едди и доступ к ИИ Шерлоку как помощнику."}]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "ИИ Шерлок, созданный на основе архивов о Холмсе, ведёт частное агентство на Бейкер-стрит. Игрок вступает в расследование убийства, балансируя между логикой машины и человеческой интуицией.\\n", "mechanics_links": [], "assets": []}, {"id": "investigation_flow", "title": "Ход расследования", "body": "1. Принять заказ от ИИ Шерлока и изучить материалы дела.\\n2. Осмотреть место преступления, преодолев проверки на расследование и восприятие.\\n3. Собрать и каталогизировать пять улик, позволяя ИИ анализировать данные.\\n4. Провести дедукцию и выйти на подозреваемого, подготовив финальное противостояние.\\n", "mechanics_links": [], "assets": []}, {"id": "unique_elements", "title": "Особенности", "body": "ИИ цитирует оригинальные произведения, офис оформлен в викторианском стиле, присутствуют камин и скрипка.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "2 500 XP, 15 000 едди и возможность призывать ИИ Шерлока как помощника в дальнейших делах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-sherlock-ai"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «ИИ Шерлок» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

