nition": {"quest_type": "side", "level_min": 15, "level_max": 30, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "explore_bilingual_signage", "text": "Исследовать двуязычные надписи в Брюсселе и понять практику параллельного использования языков", "type": "explore", "target": "brussels_bilingual_signs", "count": 1, "optional": false}, {"id": "visit_political_center", "text": "Посетить информационный центр о бельгийской политике и изучить историю политических кризисов", "type": "investigate", "target": "belgian_politics_info_center", "count": 1, "optional": false}, {"id": "compare_regional_differences", "text": "Изучить экономические и культурные различия между Фландрией и Валлонией", "type": "investigate", "target": "regional_economic_comparison", "count": 1, "optional": false}, {"id": "interact_with_communities", "text": "Пообщаться с представителями обеих языковых общин и понять их аргументы", "type": "interact", "target": "community_representatives", "count": 2, "optional": false}, {"id": "analyze_unity_threats", "text": "Проанализировать угрозы единству Бельгии и возможные сценарии развития", "type": "investigate", "target": "belgian_unity_analysis", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": 0, "items": [{"item_id": "achievement_belgian_crisis_expert", "quantity": 1}], "reputation": {"Brussels_Politics": 25, "European_Diplomacy": 20}, "unlocks": ["political_quests", "diplomacy_training"]}, "branches": [{"branch_id": "political_analyst", "description": "Стать политическим аналитиком", "consequences": {"skills": {"politics": 25, "diplomacy": 20, "culture": 15}, "reputation": {"Brussels_Politics": 30, "European_Diplomacy": 25}, "unlocks": ["exclusive_political_events", "diplomatic_network"]}}]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Бельгия - страна с глубоким языковым разделением между франкоязычной Валлонией и\\nнидерландоязычной Фландрией. Брюссель служит двуязычным мостом между общинами,\\nно политические кризисы постоянно напоминают о хрупкости этого баланса.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы квеста", "body": "1. **Двуязычные надписи**: Исследование параллельного использования французского и нидерландского.\\n2. **Политический центр**: Изучение истории кризисов и рекордных периодов без правительства.\\n3. **Региональные различия**: Сравнение экономики и культуры Фландрии и Валлонии.\\n4. **Общение с общинами**: Диалог с представителями разных языковых групп.\\n5. **Анализ угроз**: Осмысление возможных сценариев развития Бельгии.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Политическая реальность Брюсселя", "body": "Брюссель - единственный регион Бельгии с официальным двуязычием. Политическая система\\nпостроена на сложном балансе между общинами, где каждый кризис подчеркивает глубину разделения.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "- **Опыт**: 3 000 XP\\n- **Валюта**: 0 (политический квест)\\n- **Предметы**: Ачивка «Знаток бельгийского кризиса»\\n- **Репутация**: +25 к политической репутации Брюсселя, +20 к европейской дипломатии\\n- **Разблокировки**: Доступ к политическим квестам и дипломатическому обучению\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Игровые механики", "body": "Квест сочетает образовательные элементы, социальные взаимодействия и политический анализ.\\nИгрок учится понимать сложности многоязычного общества и дипломатического баланса.\\n", "mechanics_links": [], "assets": []}, {"id": "connections", "title": "Связи с другими квестами", "body": "Связан с политической линией Брюсселя и европейскими дипломатическими квестами. Открывает\\nпуть к более глубокому пониманию европейской интеграции и культурных конфликтов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 56, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-13", "author": "content_writer", "changes": "Структурирован квест согласно архитектуре системы квестов. Добавлена quest_definition с objectives, rewards и branches. Сфокусирован на политических и социальных аспектах бельгийского двуязычия."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

