INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-tashkent-2029-samsa-quest', 'Ташкент 2020-2029 — Квест самсы', 'Игрок расспрашивает местных, проходит дегустационный маршрут, сравнивает рецепты и завершает мастер-классом по лепке.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "find_best_tandyr_houses", "text": "Собрать рекомендации у жителей, сравнить рейтинги и сформировать список из трёх легендарных тандырных", "type": "interact", "target": "tashkent_tandyr_scouting", "count": 1, "optional": false}, {"id": "complete_tasting_route", "text": "Продегустировать самсу с бараниной, тыквой и картофелем, оценить температуру, сочность и специи", "type": "consume", "target": "samsa_tasting_route", "count": 3, "optional": false}, {"id": "choose_winner", "text": "Заполнить карточку жюри, поделиться отзывом и получить реакции владельцев", "type": "interact", "target": "samsa_judging_ceremony", "count": 1, "optional": false}, {"id": "complete_masterclass", "text": "Пройти мастер-класс по лепке: формовать самсу, делать узор чекичем и правильно прилеплять к стенке печи", "type": "training", "target": "samsa_masterclass", "count": 1, "optional": false}]'::jsonb, '{"experience": 600, "currency": -15, "items": [{"id": "satiety_buff", "name": "Бафф «Сытность» +15% HP на 6 часов", "quantity": 1}, {"id": "samsa_recipe", "name": "Рецепт самсы", "quantity": 1}], "reputation": {}, "flags": ["samsa_master_title"]}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-tashkent-2029-samsa-quest", "title": "Ташкент 2020-2029 — Квест самсы", "document_type": "canon", "category": "timeline-quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-11-29T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tashkent", "cuisine", "social"], "topics": ["culinary_tour", "community"], "related_systems": ["narrative-service", "liveops-service", "progression-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/cis/tashkent/2020-2029/quest-006-samsa-quest.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Кулинарной ветке Ташкента не хватало лёгкого городского квеста на знакомство с самсой и локальными заведениями.", "goal": "Организовать гастротур по трём лучшим тандырным, дать игроку дегустацию и обучить лепке самсы.", "essence": "Игрок расспрашивает местных, проходит дегустационный маршрут, сравнивает рецепты и завершает мастер-классом по лепке.", "key_points": ["Маршрут раскрывает разнообразие начинок и традиций приготовления.", "Механики дегустации и социального опроса вовлекают в городскую жизнь.", "Финал даёт постоянный рецепт и временный бафф от свежей самсы."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_best_tandyr_houses", "text": "Собрать рекомендации у жителей, сравнить рейтинги и сформировать список из трёх легендарных тандырных", "type": "interact", "target": "tashkent_tandyr_scouting", "count": 1, "optional": false}, {"id": "complete_tasting_route", "text": "Продегустировать самсу с бараниной, тыквой и картофелем, оценить температуру, сочность и специи", "type": "consume", "target": "samsa_tasting_route", "count": 3, "optional": false}, {"id": "choose_winner", "text": "Заполнить карточку жюри, поделиться отзывом и получить реакции владельцев", "type": "interact", "target": "samsa_judging_ceremony", "count": 1, "optional": false}, {"id": "complete_masterclass", "text": "Пройти мастер-класс по лепке: формовать самсу, делать узор чекичем и правильно прилеплять к стенке печи", "type": "training", "target": "samsa_masterclass", "count": 1, "optional": false}], "rewards": {"experience": 600, "currency": -15, "items": [{"id": "satiety_buff", "name": "Бафф «Сытность» +15% HP на 6 часов", "quantity": 1}, {"id": "samsa_recipe", "name": "Рецепт самсы", "quantity": 1}], "reputation": {}, "flags": ["samsa_master_title"]}, "branches": []}, "content": {"sections": [{"id": "scouting", "title": "Поиск лучших точек", "body": "Игрок собирает рекомендации у жителей, сравнивает рейтинги и формирует список из трёх легендарных тандырных. Социальная мини-игра\\nповышает доверие местных и открывает бонусные скидки.\\n", "mechanics_links": [], "assets": []}, {"id": "tasting", "title": "Дегустационный маршрут", "body": "Маршрут включает самсу с бараниной, тыквой и картофелем. В каждой точке игрок оценивает температуру, сочность и специи, получая\\nвременные баффы и впечатления.\\n", "mechanics_links": [], "assets": []}, {"id": "judging", "title": "Выбор победителя", "body": "После дегустации игрок заполняет карточку жюри, делится отзывом и получает реакции владельцев. Итоговый выбор влияет на репутацию\\nзаведения и открывает бонусные блюда.\\n", "mechanics_links": [], "assets": []}, {"id": "workshop", "title": "Мастер-класс по лепке", "body": "Тандырщик обучает формовать самсу, делать узор чекичем и правильно прилеплять заготовку к стенке печи. Мини-игра по таймингу проверяет\\nловкость и аккуратность.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 600 XP, -15 еддис на дегустации, бафф «Сытность» (+15% HP на 6 часов).\\n- Рецепт самсы и титул «Мастер самсы» при удачной лепке.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 788, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-29", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и branches. Детализирован контент согласно концепции. Добавлены механики кулинарного тура и сообщества."}, {"version": "1.0.0", "date": "2025-11-13", "author": "concept_team", "changes": "Квест «Квест самсы» адаптирован под шаблон Concept Director."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

