INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-lon-pub-crawl', 'Лондон 2030-2039 — Паб-краул', 'Игрок проходит по пяти культовым пабам, участвует в мини-играх, поддерживает уровень опьянения и заводит знакомства, балансируя веселье и выносливость.
', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-lon-pub-crawl", "title": "Лондон 2030-2039 — Паб-краул", "document_type": "canon", "category": "timeline-author", "status": "in-review", "version": "0.1.0", "last_updated": "2025-11-11T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["london", "nightlife", "social"], "topics": ["slice_of_life", "humor"], "related_systems": ["narrative-service", "social-system", "progression-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/europe/london/2030-2039/quest-019-pub-crawl.md", "visibility": "internal", "audience": ["narrative", "vision", "content"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Социальный квест о традиционном паб-крауле был в Markdown и не использовался в знаниевой базе.", "goal": "Структурировать расслабленный контент о ночной жизни Лондона для дальнейшей интеграции в социальные сценарии.", "essence": "Игрок проходит по пяти культовым пабам, участвует в мини-играх, поддерживает уровень опьянения и заводит знакомства, балансируя веселье и выносливость.\\n", "key_points": ["Описан маршрут по пяти культовым пабам Сохо с мини-играми и проверками навыков.", "Зафиксированы механики опьянения, социальные взаимодействия и комедийные драки.", "Определены награды в виде опыта, социальных бонусов и ачивок."]}, "content": {"sections": [{"id": "quest_overview", "title": "Концепция", "body": "Небольшой социальный квест, который показывает культурный пласт Лондона через традицию паб-краула и знакомство с колоритными NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Ход квеста", "body": "1. Начать вечер в «The Fox and Hounds» и получить маршрут.\\n2. Посетить пять пабов, выполняя мини-игры (дартс, бильярд, импровизированные драки).\\n3. Следить за уровнем опьянения, чтобы дойти до финального бара.\\n4. Завершить ночь, определив исход — проснуться дома или в канаве.\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "Система опьянения меняет характеристики, открывает новые диалоги и провоцирует случайные события с NPC.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает 600 XP, тратит до 2 000 едди и открывает социальных союзников. Дополнительно выдаётся ачивка «Британский Выпивоха».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml#canon-quest-lon-pub-crawl"], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конвертирован квест «Паб-краул» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

